"use strict";(self.webpackChunkmy_website=self.webpackChunkmy_website||[]).push([[9081],{7677:r=>{r.exports=JSON.parse('{"archive":{"blogPosts":[{"id":"modular_avatar_guide","metadata":{"permalink":"/ja/blog/modular_avatar_guide","source":"@site/blog/2025-04-27-guide-modular_avatar.md","title":"\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uac00\uc774\ub4dc","description":"\uc694\uc57d","date":"2025-04-27T00:00:00.000Z","tags":[{"inline":false,"label":"VRChat","permalink":"/ja/blog/tags/vrchat","description":"VRChat \uad00\ub828 \uc544\ud2f0\ud074"},{"inline":false,"label":"\uac15\uc88c","permalink":"/ja/blog/tags/guide","description":"VRChat \uad00\ub828 \uac15\uc88c"},{"inline":false,"label":"Unity","permalink":"/ja/blog/tags/unity","description":"Unity \uad00\ub828 \uc544\ud2f0\ud074"},{"inline":false,"label":"Modular Avatar","permalink":"/ja/blog/tags/modularavatar","description":"\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uad00\ub828 \uc544\ud2f0\ud074"}],"hasTruncateMarker":false,"authors":[{"name":"\ub9c8\ub07c\uc544\ub610","title":"VRSuya \ubaa8\ub378\ub7ec","url":"https://twitter.com/VRC_Macchiato","imageURL":"https://github.com/crestudio.png","key":"macchiato","page":null}],"frontMatter":{"slug":"modular_avatar_guide","title":"\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uac00\uc774\ub4dc","authors":"macchiato","tags":["vrchat","guide","unity","modularavatar"]},"unlisted":false,"nextItem":{"title":"VRChat \uc544\ubc14\ud0c0 \uae30\ubcf8 \uad6c\uc870\ub3c4","permalink":"/ja/blog/vrchat_avatar_component_guide"}},"content":"## \uc694\uc57d\\r\\n\\r\\n1. \uc758\uc0c1\uc774\ub098 \uae30\ubbf9\uc744 **\uc544\ubc14\ud0c0\uc5d0 \ub123\uae30 \uc678\uc5d0 \ubb54\uac00 \ub354 \ud574\uc57c \ub41c\ub2e4\uba74 \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uc138\ud305\uc774 \ub35c \ub41c \uac70\uc784**\\r\\n1. Prefab \uac1c\ub150\uc744 \uc774\ud574\ud558\uace0 \uc4f0\uba74 \uc774\ub9cc\ud55c \uc560\ub4dc\uc628\uc774 \uc5c6\uc73c\ub2c8 \uaf2d \uc368 \ubcf4\uc790\\r\\n1. \ubb34\uc2a8 \uae30\ub2a5\uc778\uc9c0 \uc54c\uae30\ub9cc \ud558\uba74 \ub428, \uc2e4\uc81c\ub85c\ub294 \ub123\uc5b4\uc57c \ub420 \ub0b4\uc6a9\uc740 \uadf8\ub2e4\uc9c0 \uc5c6\uc74c\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \uc11c\ub860\\r\\n\\r\\nVRChat \uc544\ubc14\ud0c0\uc5d0 \uc637\uc744 \uc785\ud788\ub824\uba74 \uc544\ubc14\ud0c0\uc640 \uc637 \ubaa8\ub378\uc744 \ube14\ub80c\ub354\uc5d0\uc11c \ud569\uccd0\uc11c \uc0c8\ub85c\uc6b4 \ubaa8\ub378 \ud30c\uc77c\uc744 \ub9cc\ub4dc\ub294\uac8c \uac00\uc7a5 \uae30\ubcf8\uc801\uc774\uace0 \uc81c\uc77c \uc88b\uc740 \ubc29\ubc95\uc784, \ud558\uc9c0\ub9cc \ube14\ub80c\ub354\ub294 \uc5b4\ub835\uace0 \uc720\ub2c8\ud2f0\uc5d0\uc11c \uc5b4\ub5bb\uac8c \uc637\uc744 \uc785\ud790 \ubc29\ubc95\uc774 \uc5c6\uc744\uae4c \uc0ac\ub78c\ub4e4\uc774 \uc5f0\uad6c\ud558\ub2e4\uac00 \ubc29\ubc95\uc744 \ubc1c\uacac\ud568\\r\\n\\r\\n**\uc544\ubc14\ud0c0\uc758 \ud734\uba38\ub178\uc774\ub4dc \ubcf8\uc5d0\ub2e4\uac00 \uc637\uc758 \ubcf8\uc744 \ub611\uac19\uc774 \ub9e4\uce6d**\ud574\uc11c \ub123\uc5b4\uc8fc\uba74 \ube14\ub80c\ub354\ub97c \uac70\uce58\uc9c0 \uc54a\uace0\ub3c4 \uc637\uc744 \uc785\ud790 \uc218 \uc788\ub2e4\ub294 \uc0ac\uc2e4\uc744 \ubc1c\uacac\ud55c\uac70\uc784, \uadf8\ub798\uc11c \ubcf8\uc744 \uc9c1\uc811 \uc77c\uc77c\uc774 \ub123\uae30\ub294 \uadc0\ucc2e\uc73c\ub2c8\uae4c [\uc790\ub3d9\uc73c\ub85c \ubcf8\uc744 \ub9de\ucdb0\uc11c \ub123\uc5b4\uc8fc\ub294 \uc720\ub2c8\ud2f0 \uc2a4\ud06c\ub9bd\ud2b8](https://unsafelink.com/https://kurotori.booth.pm/items/1564788)\uac00 \ub098\uc624\uace0 \uadf8\ub7ac\uc5c8\uc74c, \ud558\uc9c0\ub9cc \uc774\ub7f0 \ub178\uac00\ub2e4\ub97c \ud558\uba74 \ubb38\uc81c\uac00 \uc788\uc73c\ub2c8 \ubc14\ub85c \uc758\uc0c1\uc744 \ub2e4\uc2dc \ube7c\uac70\ub098 \ubb54\uac00 \ubcc0\uacbd\uc744 \ud558\ub824\uace0 \ud558\uba74 \uc544\ubc14\ud0c0\uc758 \ubcf8 \ud558\uc704\uc5d0 \ub4e4\uc5b4\uac00\uc11c \ub2e4\uc2dc \ub2e4 \ube7c\uc57c \ud558\uace0, \uc637\uc744 \ub9ce\uc774 \ub123\uc5c8\uc73c\uba74 \ub108\ubb34 \ub9ce\uc740 \ubcf8\ub4e4\uc774 \uc788\uc5b4\uc11c \ubb50\uac00 \ubb54\uc9c0 \ubab0\ub77c\uc11c \ud5f7\uac08\ub9ac\uae30 \uc2dc\uc791\ud55c \uac70\uc784\\r\\n\\r\\n\uc2e4\uc81c \uc138\uc0c1\uc5d0\uc11c\ub294 \uc637\uc740 \ub2e8\uc21c\ud788 \uc785\uace0 \ubc97\uc73c\uba74 \ub418\ub294\ub370 3D \uc544\ubc14\ud0c0\ub294 \uc637\uc744 \uc785\ud788\ub294\ub370 \ud55c \uc138\uc6d4 \uac78\ub9ac\ub2c8\uae4c, \uc5b4\ub5bb\uac8c \ud558\uba74 \ub2e8\uc21c\ud558\uac8c \uc637\uc744 \uc785\ud790 \uc218 \uc788\uc744\uae4c \ud558\ub2e4\uac00 [\uc77c\ubcf8\uc758 \uac1c\ubc1c\uc790](https://twitter.com/bd_j)\uac00 [\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0](https://modular-avatar.nadena.dev/)\ub97c \ub9cc\ub4e4\uc5b4\uc11c \ubb34\ub8cc\ub85c \ubc30\ud3ec\ud558\uace0 \ud604\uc7ac\uae4c\uc9c0 \uacc4\uc18d \uc5c5\ub370\uc774\ud2b8 \ud574 \uc8fc\uace0 \uc788\uc74c\\r\\n\\r\\n\uadf8\ub798\uc11c \uc774\ubc88\uc5d0\ub294 \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uc560\ub4dc\uc628\uc5d0\uc11c \uc790\uc8fc \uc0ac\uc6a9\ud558\ub294 \uae30\ub2a5\ub4e4\uc744 \uc54c\uc544\ubcf4\uc790\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0(Modular Avatar) \uac1c\ub150\\r\\n\\r\\n\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\ub294 **\ud504\ub9ac\ud339(Prefab)\uc744 \uae68\uc9c0 \uc54a\uace0 \uc544\ubc14\ud0c0\uc5d0 \uc637\uc774\ub098 \uae30\ubbf9\ub4e4\uc744 \ub123\uc744 \uc218 \uc788\uac8c \ud574 \uc8fc\ub294 \uc560\ub4dc\uc628**\uc774\ub77c\uace0 \ubcf4\uba74 \ub428\\r\\n\\r\\n\uc11c\ub860\ucc98\ub7fc \uc544\ubc14\ud0c0\uc5d0 \uc637 \ubcf8\uc744 \ub123\uc73c\ub824\uba74 \ud504\ub9ac\ud339\uc744 \uc5b8\ud329\ud55c \ub2e4\uc74c\uc5d0 \ub123\uc5b4\uc57c \ub418\ub294\ub370, **\ud504\ub9ac\ud339\uc744 \uc5b8\ud329\ud558\uae30 \uc804\uc73c\ub85c \uc808\ub300 \ub418\ub3cc\ub9b4 \uc218 \uc5c6\uae30 \ub54c\ubb38\uc5d0 \ud30c\uad34\uc801(Destructive)\uc778 \ubc29\uc2dd**\uc774\ub77c\uace0 \ud558\uace0, \uc5f0\uacb0\uc774 \ub04a\uc5b4\uc84c\uae30 \ub54c\ubb38\uc5d0 \uc758\uc0c1\uc774 \ud328\uce58 \ub418\uac70\ub098 [Prefab \uac15\uc88c](./2025-04-07-guide-prefab.md)\ucc98\ub7fc \ud504\ub9ac\ud339\uacfc \uad00\ub828\ub41c \uc720\uc6a9\ud55c \uae30\uc220\uc744 \uc0ac\uc6a9\ud560 \uc218 \uc5c6\uc5b4\uc11c \uc798\ubabb\ub418\uba74 \uc544\ubc14\ud0c0\ub97c \uafb8\ubbf8\ub294 \uac83\ubcf4\ub2e4 \uace0\uc7a5\ub098\uba74 \uc5b4\ub514\uac00 \uace0\uc7a5 \ub0ac\ub294\uc9c0 \ucc3e\ub294\ub370 \uc2dc\uac04\uc774 \uc624\ub798 \uac78\ub9ac\uac8c \ub418\ub294 \ubd80\uc791\uc6a9\uc774 \uc0dd\uae40\\r\\n\\r\\n\ud558\uc9c0\ub9cc \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\ub294 **\ud50c\ub808\uc774\ub098 \uc5c5\ub85c\ub4dc \ub2e8\uacc4\uc5d0\uc11c\ub9cc \uac00\uc0c1\uc73c\ub85c \uc801\uc6a9\ub41c \uc0c1\ud0dc**\ub97c \ub9cc\ub4e4\uc5b4 \uc8fc\uace0, \uc720\ub2c8\ud2f0 \uc0c1\uc5d0\uc11c\ub294 \ud504\ub9ac\ud339 \uad6c\uc870\ub97c \uadf8\ub300\ub85c \ub458 \uc218 \uc788\uc73c\ub2c8 \uc544\ubc14\ud0c0\uc640 \uc637 \ub0b4\uc6a9\uc774 \uc11c\ub85c \uc11e\uc77c \uc77c\uc774 \uc5c6\uc5b4\uc11c \uad6c\uc870\uc801\uc73c\ub85c\ub3c4 \ub208\uc73c\ub85c\ub3c4 \ubcf4\uae30\uc5d0\ub3c4 \uc88b\uc544\uc9c0\ub2c8 \uad00\ub9ac\ud558\uae30 \ud6e8\uc52c \ub354 \uc26c\uc6cc\uc9c0\ub294 \uac83\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Prefab](./modularavatar/Unity_Modular%20Avatar_Prefab.jpg)\\r\\n\\r\\n[CityChic](https://eliya.booth.pm/items/5329019)\ub294 \uc624\ud53c\uc2a4\ub8e9 \uc758\uc0c1\uc778\ub370, **\uc758\uc0c1\uacfc \uad00\ub828\ub41c \ub0b4\uc6a9\uc740 \uc800 \uc548\uc5d0\ub9cc \uc874\uc7ac\ud558\uace0 \uc6d0\ubcf8 \uc544\ubc14\ud0c0\uc5d0\ub294 \uc874\uc7ac\ud558\uc9c0 \uc54a\uae30 \ub54c\ubb38**\uc5d0 \uc637\uacfc \uad00\ub828\ub41c \ub0b4\uc6a9\uc744 \uc218\uc815\ud558\ub824\uba74 \uc800 \ud504\ub9ac\ud339 \ub0b4\ubd80\ub9cc \uc870\uc0ac\ud558\uba74 \ub428, \ub610 \uc758\uc0c1\uc744 \uc81c\uac70\ud558\ub824\uba74 \uac04\ub2e8\ud788 \uc800 \ud504\ub9ac\ud339\ub9cc \uc9c0\uc6b0\uba74 \uae54\ub054\ud558\uac8c \uc5c6\uc5b4\uc9d0\\r\\n\\r\\n\ub9cc\uc57d \ubaa8\ub450 \ubcd1\ud569\ub41c \uc0c1\ud0dc\ub77c\uba74 FX \ub808\uc774\uc5b4\uc758 \ub370\uc774\ud130\ub3c4 \uc9c0\uc6cc\uc57c \ud558\uace0 \ud30c\ub77c\uba54\ud130\ub3c4 \uc9c0\uc6b0\uace0 \ubcf8\ub3c4 \uc9c0\uc6cc\uc57c \ud558\uace0 \uc758\uc0c1 \uc624\ube0c\uc81d\ud2b8\ub3c4 \ub610 \ucc3e\uc544\uc11c \uc9c0\uc6cc\uc57c \ud558\uace0, \ub098\ub3c4 \ubaa8\ub974\uac8c \ubc14\ub010\uac70 \uc788\uc73c\uba74 \uadf8\ub7f0 \ub370\uc774\ud130\ub3c4 \ub0a8\uac8c \ub418\ub2c8 \uc774\uac8c \ub098\uc911\uc5d0 \ubb34\uc2a8 \ubc84\uadf8\ub97c \uc77c\uc73c\ud0ac \uc9c0 \ubaa8\ub974\uaca0\uc8e0?\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uae30\ubcf8 \ud15c\ud50c\ub9bf\\r\\n\\r\\n![Cloth](./modularavatar/Unity_Modular%20Avatar_Cloth.jpg)\\r\\n![Accessory](./modularavatar/Unity_Modular%20Avatar_Accessory.jpg)\\r\\n\\r\\n\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\uc758 \uac01 \uad6c\uc131\uc694\uc18c\ub97c \uc0b4\ud3b4\ubcf4\uae30 \uc804\uc5d0 \uc774\ub7ec\ud55c \ub290\ub08c\uc73c\ub85c \uc758\uc0c1 \ud504\ub9ac\ud339\uacfc \uc561\uc138\uc11c\ub9ac \ud504\ub9ac\ud339\uc744 \ub9cc\ub4dc\ub294\uac8c \ubaa9\ud45c\ub77c \uc0dd\uac01\ud558\uace0 \uc77d\uc73c\uba74 \uc774\ud574\ud558\uae30 \uc88b\uc744 \ub4ef\\r\\n\\r\\n\uc5ec\uae30\uc11c \ud544\uc694\ud558\uba74 \ub354 \ub123\uac70\ub098 \ube7c\ub294 \ub290\ub08c\uc73c\ub85c \uc791\uc5c5\ud574\uc11c \ub0b4\uac00 **\uc6d0\ud558\ub294 \uae30\ub2a5\uc744 \uac00\uc9c4 \ud504\ub9ac\ud339\uc744 \ub9cc\ub4dc\ub294\uac8c \ucd5c\uc885 \ubaa9\ud45c**\ub77c\uace0 \ubcf4\uba74 \ub428\\r\\n\\r\\n\uc880 \ub354 \uc27d\uac8c \uc124\uba85\ud558\uc790\uba74 \uadf8\ub0e5 **\uc544\ubc14\ud0c0\uc5d0 \uc644\uc131\ub41c \ubaa8\ub4c8\ub7ec \ud504\ub9ac\ud339 \ub123\uc73c\uba74 \uc138\ud305 \ub05d**\uc774\uc5b4\uc57c \ud568, \uadf8\uac8c \uc544\ub2c8\uba74 \ubb54\uac00 \ub35c \uc138\ud305\ud588\ub2e4\ub294 \ub73b\uc784\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \uc790\uc8fc \uc0ac\uc6a9\ud558\ub294 \ucef4\ud3ec\ub10c\ud2b8\\r\\n\\r\\n\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\uc5d0\uc11c \ud504\ub9ac\ud339 \ub9cc\ub4e4 \ub54c \uc790\uc8fc \uc0ac\uc6a9\ud558\ub294 \ucef4\ud3ec\ub10c\ud2b8 \uc704\uc8fc\ub85c \uc791\uc131\ud568, \ub2e4\ub978 \ucef4\ud3ec\ub10c\ud2b8\ub098 \ub354 \uc790\uc138\ud558\uac8c \uc54c\uace0 \uc2f6\uc73c\uba74 [\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uacf5\uc2dd \ubb38\uc11c](https://modular-avatar.nadena.dev/docs/reference)\ub97c \ubc88\uc5ed\uae30 \ub3cc\ub824\uc11c \ubd10\ub3c4 \uc88b\uc74c\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Blendshape Sync\\r\\n\\r\\n![Blendshape Sync](./modularavatar/Unity_Modular%20Avatar_Blendshape%20Sync_1.jpg)\\r\\n![Blendshape Sync](./modularavatar/Unity_Modular%20Avatar_Blendshape%20Sync_2.jpg)\\r\\n\\r\\n\uc8fc\ub85c **\ubc14\ub514 \uc250\uc774\ud504\ud0a4\uc5d0 \ub9de\ucdb0\uc11c \uc637\uc758 \uc250\uc774\ud504\ud0a4 \uc218\uce58\ub97c \ub3d9\uae30\ud654**\ud560 \ub54c \uc500\\r\\n\\r\\n\ubc14\ub514\uc758 \uac00\uc2b4\uc774 \ucee4\uc9c0\uba74, \uc637\ub3c4 \uac00\uc2b4\uc5d0 \ub9de\ucdb0\uc11c \ucee4\uc838\uc57c \uc54c\ubab8 \uc548 \ubcf4\uc774\ub294 \uac83\ucc98\ub7fc \uc11c\ub85c \uc250\uc774\ud504\ud0a4 \uc218\uce58\ub97c \ub3d9\uae30\ud654\uac00 \ud544\uc694 \ud560 \ub54c \uc500\\r\\n\\r\\n\uc250\uc774\ud504\ud0a4\uac00 \uc11c\ub85c \uc774\ub984\uc774 \ub2e4\ub978 \uac70\ub294 \uc0c1\uad00 \uc5c6\ub294\ub370, \ub300\uc2e0 \uc11c\ub85c \ubc18\ub300\ub85c \ub3d9\uae30\ud654 \ud558\uac70\ub098 \uc808\ubc18\ub9cc \ucc2c\ub2e4\ub358\uac00 \ud558\ub294 \uadf8\ub7f0 \uae30\ub2a5\uc740 \uc5c6\uc74c\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Bone Proxy\\r\\n\\r\\n![Bone Proxy](./modularavatar/Unity_Modular%20Avatar_Bone%20Proxy_1.jpg)\\r\\n![Bone Proxy](./modularavatar/Unity_Modular%20Avatar_Bone%20Proxy_2.jpg)\\r\\n\\r\\n\ucd1d\uc774\ub098 \ud734\ub300\ud3f0, \uba38\ub9ac\ud540\uacfc \uac19\uc740 **\uc624\ube0c\uc81d\ud2b8\ub97c \uc5b4\ub5a0\ud55c \ubcf8\uc5d0 \uace0\uc815**\ud558\uace0 \uc2f6\uc744 \ub54c \uc500\\r\\n\\r\\n\ubb3c\ub860 \uc548 \uc4f0\uace0 \uc9c1\uc811 \uc190\uc774\ub098 \uba38\ub9ac\uc5d0 \ub123\uc5b4\ub3c4 \ub418\uae34 \ud558\ub294\ub370, \uc774\uac78\ub85c \uc138\ud305\ud558\uba74 \uad73\uc774 \ud574\ub2f9 \ubcf8\uae4c\uc9c0 \ucc3e\uc544\uac00\uc11c \ud655\uc778\ud560 \ud544\uc694 \uc5c6\uc774 \uc544\ubc14\ud0c0 \ub8e8\ud2b8\uc5d0\uc11c \ubaa8\ub4c8\ub4e4\uc744 \ud55c \ubc88\uc5d0 \ubcfc \uc218 \uc788\uc73c\ub2c8\uae4c \uad00\ub9ac\ud558\uae30 \ud3b8\ud574\uc9d0\\r\\n\\r\\n\ub610\ud55c \uc544\ub798\uc758 [Merge Animator](#merge-animator)\uc640 \uac19\uc774 \uc0ac\uc6a9\ud558\uba74 \uba38\ub9ac\uac00 \uc544\ub2c8\ub77c \ub2e4\ub978 \uacf3\uc5d0 \ub4e4\uc5b4\uac00\uac8c \ubc14\uafd4\ub3c4 \uc138\ud305\ud55c \uc560\ub2c8\uba54\uc774\uc158 \uacbd\ub85c\ub97c \ubc14\uafc0 \ud544\uc694\uac00 \uc5c6\uc73c\ub2c8 \ub9e4\uc6b0 \ud3b8\ub9ac\ud558\ub2e4\\r\\n\\r\\n\uc0ac\uc6a9 \ubc29\ubc95\uc740 **\ubd80\ucc29\uc744 \uc6d0\ud558\ub294 \ubcf8\uc744 \uc9c0\uc815\ud558\uace0, \uc624\ube0c\uc81d\ud2b8\ub97c \uc6d0\ud558\ub294 \uc704\uce58\uc5d0 \uc124\uc815\ud55c \ub2e4\uc74c, \uc704\uce58\ub098 \ud68c\uc804\uae4c\uc9c0 \uadf8\ub300\ub85c \ubc18\uc601**\ud558\uba74 \ub41c\ub2e4\\r\\n\\r\\n\uac00\uc2b4 \ubcf8 \uac19\uc740 \ud2b9\uc218\ud55c \ubcf8\uc740 \ubd80\ubaa8 \ubcf8\uc778 Chest\ub97c \uc120\ud0dd\ud558\uace0 ``Breast_Root/Breast_L`` \uac19\uc774 \ud558\uc704 \ubcf8 \uacbd\ub85c\ub97c \uc815\ud655\ud558\uac8c \ub123\uc5b4\uc8fc\uba74 \ub41c\ub2e4, \uc81c\ub300\ub85c \uc785\ub825\ud588\uc73c\uba74 \uc704\uc758 Target\uc5d0 \ubcf8\uc774 \uc81c\ub300\ub85c \ud45c\uc2dc \ub420 \uac70\uc784\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Menu Installer\\r\\n\\r\\n![Menu Installer](./modularavatar/Unity_Modular%20Avatar_Menu%20Installer_1.jpg)\\r\\n![Menu Installer](./modularavatar/Unity_Modular%20Avatar_Menu%20Installer_2.jpg)\\r\\n\\r\\n**\uc544\ubc14\ud0c0\uc758 \uba54\ub274\uc5d0 \ud574\ub2f9 \uba54\ub274 \uc5d0\uc14b\uc744 \ucd94\uac00** \ud574 \uc90c\\r\\n\\r\\n\uac1c\uc778\uc801\uc73c\ub85c \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\ub85c \uba54\ub274 \ub9cc\ub4dc\ub294 \uac83\ubcf4\ub2e4 \uc9c1\uc811 \uba54\ub274 \ub9cc\ub4dc\ub294\uac8c \uc880 \ub354 \ud3b8\ud574\uc11c \uc774\uac78 \ub9ce\uc774 \uc500\\r\\n\\r\\n\uae30\ubcf8\uc801\uc73c\ub85c \ucd5c\uc0c1\ub2e8 \uba54\ub274\uc5d0 \ucd94\uac00\uac00 \ub418\ub294\ub370, \uba54\ub274 \uc120\ud0dd \ubc84\ud2bc\uc73c\ub85c \uc6d0\ud558\ub294 \uc11c\ube0c \uba54\ub274 \uc548\uc5d0 \ub4f1\ub85d\ub418\uac8c \uc218\uc815\ud560 \uc218 \uc788\uc74c, \ub610\ud55c \uba54\ub274 \uac2f\uc218 \ucd08\uacfc\ud558\uba74 \uc54c\uc544\uc11c \ub2e4\uc74c \ud398\uc774\uc9c0\ub97c \ub9cc\ub4e4\uc5b4 \uc900\ub2e4\\r\\n\\r\\n\uc0ac\uc6a9 \ubc29\ubc95\uc740 \uadf8\ub0e5 \uc6d0\ud558\ub294 **\uba54\ub274 \ud30c\uc77c\uc744 \uac1c\ubc1c\uc790\uc6a9 \uba54\ub274 \uc5f4\uba74 \ub098\uc624\ub294 \ube48 \uce78\uc5d0** \ub123\uc5b4\uc8fc\uba74 \ub05d\uc774\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Merge Animator\\r\\n\\r\\n![Merge Animator](./modularavatar/Unity_Modular%20Avatar_Merge%20Animator_1.jpg)\\r\\n![Merge Animator](./modularavatar/Unity_Modular%20Avatar_Merge%20Animator_2.jpg)\\r\\n\\r\\n\uc8fc\ub85c **\uae30\uc874 \uc560\ub2c8\uba54\uc774\ud130\uc5d0 \uc6d0\ud558\ub294 \uc560\ub2c8\uba54\uc774\ud130 \ub808\uc774\uc5b4\ub4e4\uc744 \ucd94\uac00**\ud558\ub294 \uae30\ub2a5\uc744 \ud55c\ub2e4\\r\\n\\r\\n\ucd5c\uc2e0 \ubc84\uc804\uc5d0\uc11c\ub294 \ud1b5\uc9f8\ub85c \uc560\ub2c8\uba54\uc774\ud130\ub97c \uc544\uc608 \uad50\uccb4\ud558\ub294 \uae30\ub2a5\uae4c\uc9c0 \uc0dd\uacbc\ub2e4, \uc8fc\ub85c Action \ub808\uc774\uc5b4\ub098 Locomotion(Base) \ub808\uc774\uc5b4\uc5d0\uc11c \ub9ce\uc774 \uc4f8 \ub4ef\\r\\n\\r\\n\uc0ac\uc6a9 \ubc29\ubc95\uc740 **\ubcd1\ud569\uc744 \uc6d0\ud558\ub294 \uc560\ub2c8\uba54\uc774\ud130\uc640 \ubb34\uc2a8 \ub808\uc774\uc5b4\uc5d0 \uc124\uce58 \ub420 \uc9c0 \uc815\ub3c4 \uc138\ud305**\ud558\uba74 \ub418\uace0, \uc624\ud788\ub824 **\uc560\ub2c8\uba54\uc774\ud130\uc640 \uc560\ub2c8\uba54\uc774\uc158 \uacbd\ub85c\ub97c \uc774\ud574\ud558\uace0 \uc791\uc131\ud558\ub294\uac8c \ub354 \uc911\uc694**\ud558\ub2e4\\r\\n\\r\\n\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\uc758 \uac00\uc7a5 \ud575\uc2ec \uae30\ub2a5 \uc911\uc5d0 \ud558\ub098\ub77c\uc11c \uc774\uac70\ub294 \ubc18\ub4dc\uc2dc \uc774\ud574\ud558\ub294\uac8c \uc88b\ub2e4\\r\\n\\r\\n\\r\\n\uc57d\uac04\uc758 \uac1c\ub150 \uc774\ud574\uac00 \ud544\uc694\ud55c\ub370, **\uc0c1\ub300 \uacbd\ub85c\ub77c\ub294 \uae30\ub2a5\uc73c\ub85c \uc560\ub2c8\uba54\uc774\uc158 \uacbd\ub85c\ub97c \uc54c\uc544\uc11c \uc218\uc815 \ud574 \uc8fc\ub294 \uae30\ub2a5**\uc774 \uc788\ub2e4\\r\\n\\r\\n\uc67c\uc190\uc5d0 \ub4dc\ub294 \ud734\ub300\ud3f0(Phone)\uc744 \uc870\uc791\ud558\ub294 \uc544\uc774\ud15c\uc744 \ubaa8\ub4c8\ub7ec\ub85c \ub9cc\ub4e4\uc5c8\ub2e4\uace0 \ud574 \ubcf4\uc790\\r\\n\\r\\n\uadf8\ub7fc [\ubaa8\uc5d0 \uc544\ubc14\ud0c0](https://kyubihome.booth.pm/items/4667400)\ub97c \uae30\uc900\uc73c\ub85c ``Armature/Hips/Spine/Chest/Left shoulder/Left arm/Left elbow/Left wrist/Phone`` \uc774\ub77c\ub294 \uacbd\ub85c\ub97c \uac80\uc0c9\ud574\uc57c \ud734\ub300\ud3f0\uc744 \ucc3e\uc744 \uc218 \uc788\uc744 \uac83\uc774\ub2e4, \ud558\uc9c0\ub9cc \ub300\ubd80\ubd84 \uc544\ubc14\ud0c0\ub4e4\uc740 \uc774\ub7ec\ud55c \uacbd\ub85c\uac00 \uc81c\uac01\uac01\uc774\uace0 \uc2ec\uc9c0\uc5b4 \ub300\uc18c\ubb38\uc790\uae4c\uc9c0 \uad6c\ubd84\ud55c\ub2e4\\r\\n\\r\\n\uc774\uac78 **Prefab \uae30\uc900\uc73c\ub85c \ubc14\uafd4\uc8fc\uba74, \ud574\ub2f9 \ucef4\ud3ec\ub10c\ud2b8\uac00 \uc54c\uc544\uc11c \uac8c\uc784 \uc624\ube0c\uc81d\ud2b8\uac00 \uc774\ub9ac\uc800\ub9ac \uc774\ub3d9\ud558\ub354\ub77c\ub3c4 \uc560\ub2c8\uba54\uc774\uc158 \ud074\ub9bd\uc758 \uacbd\ub85c\ub97c \uc544\ubc14\ud0c0 \uc0c1\ud0dc\uc5d0 \ub9de\uac8c \uc5c5\ub370\uc774\ud2b8**\ub97c \ud574 \uc900\ub2e4\\r\\n\\r\\n\ucc38\uc73c\ub85c \uc2e0\ubb18\ud55c \uae30\ub2a5\uc774 \uc544\ub2d0 \uc218\uac00 \uc5c6\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Merge Animator](./modularavatar/Unity_Modular%20Avatar_Merge%20Animator_3.jpg)\\r\\n\\r\\n\uac04\ub2e8\ud558\uac8c \uc815\ub9ac\ud558\uc790\uba74 **\ud504\ub9ac\ud339 \ub8e8\ud2b8\uc5d0 \uc560\ub2c8\uba54\uc774\ud130(Animator) \ucef4\ud3ec\ub10c\ud2b8\uc640 \ud574\ub2f9 \uc560\ub2c8\uba54\uc774\ud130\ub97c \ub123\uace0 \uc560\ub2c8\uba54\uc774\uc158\uc744 \uc791\uc131**\ud558\uba74 \ub41c\ub2e4\\r\\n\\r\\n\uadf8\ub798\uc11c \uc791\uc5c5\uc6a9\uc774\ub098 \uc758\uc0c1\uc5d0\uc11c \uc790\ub3d9 \uc0dd\uc131\ub418\ub294 \uc560\ub2c8\uba54\uc774\ud130 \ucef4\ud3ec\ub10c\ud2b8\ub97c \uc54c\uc544\uc11c \uc9c0\uc6cc\uc8fc\ub294 Delete attached animator \uae30\ub2a5\ub3c4 \uc788\ub2e4\\r\\n\\r\\n\ub9cc\uc57d \uc5bc\uad74 \ud45c\uc815 \uc250\uc774\ud504\ud0a4 \uac19\uc774 **\ud504\ub9ac\ud339\uacfc \uc0c1\uad00 \uc5c6\uc774 \ubc14\ub00c\uc9c0 \uc54a\ub294 \uacbd\ub85c\uac00 \ud544\uc694\ud560 \ub54c\uc5d0\ub294 \uc808\ub300 \uacbd\ub85c\ub97c \uc0ac\uc6a9**\ud558\uba74 \ub41c\ub2e4\\r\\n\\r\\n:::info\\r\\n\\r\\n\ub9cc\uc57d \uc0c1\ub300 \uacbd\ub85c\uc640 \uc808\ub300 \uacbd\ub85c\ub97c \ud63c\ud569\ud574\uc11c \uc0ac\uc6a9\ud574\uc57c \ub41c\ub2e4\uba74, \ubcc4\ub3c4\uc758 Merge Animator \ucef4\ud3ec\ub10c\ud2b8\ub85c \ubd84\ub9ac\ud574\uc11c \uc791\uc131\ud574\uc57c \ud55c\ub2e4\\r\\n\\r\\n:::\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Merge Armature\\r\\n\\r\\n![Merge Armature](./modularavatar/Unity_Modular%20Avatar_Merge%20Armature_1.jpg)\\r\\n![Merge Armature](./modularavatar/Unity_Modular%20Avatar_Merge%20Armature_2.jpg)\\r\\n\\r\\n**\uc544\ubc14\ud0c0\uc758 \uc544\ub9c8\ud29c\uc5b4\ub791 \uc637\uc758 \uc544\ub9c8\ud29c\uc5b4\ub97c \ubcd1\ud569**\ud560 \ub54c \uc0ac\uc6a9\ud568\\r\\n\\r\\n\uc6b0\ud074\ub9ad\ud574\uc11c Setup outfit \ub204\ub974\uba74 \uc637\uc758 \uc544\ub9c8\ud29c\uc5b4\uc5d0 \uc774 \ucef4\ud3ec\ub10c\ud2b8\uac00 \uc790\ub3d9\uc73c\ub85c \ub4f1\ub85d\ub418\uba74\uc11c \uc138\ud305 \ub418\ub294 \uac70\uc784, \uc9c1\uc811 \ub9cc\ub4e4\uc5b4\uc11c \ub123\uc5b4\ub3c4 \uc0c1\uad00 \uc5c6\ub2e4\\r\\n\\r\\n\uc0ac\uc6a9 \ubc29\ubc95\uc740 \uadf8\ub0e5 **\uc544\ubc14\ud0c0\uc758 Armature \uc624\ube0c\uc81d\ud2b8\ub97c \ub123\uace0, \uc544\ubc14\ud0c0(Avatar) > \uc758\uc0c1(Target) \uc801\uc6a9**\ud558\uba74 \ub05d\\r\\n\\r\\n\uc9c1\uc811 \ubaa8\ub378\uc744 \ub9cc\ub4e4\uc5b4\uc11c \ub123\ub294 \uacbd\uc6b0\uc5d0\ub294 \ubcf8\uc758 \uad6c\uc870(\uc0c1\uc18d \uad00\uacc4)\uac00 \uc11c\ub85c \ub2e4\ub974\uba74 \uc77c\uce58\ud558\ub294 \ubd80\ubd84\ub9cc \uc791\ub3d9 \ub418\ub2c8\uae4c, \uc548 \uc6c0\uc9c1\uc778\ub2e4\uba74 \uc758\uc0c1 \ubaa8\ub378\uc758 \ubcf8 \uad6c\uc870\uac00 \uc77c\uce58\ud558\ub294\uc9c0 \ud655\uc778\ud558\uc790\\r\\n\\r\\n\ubd80\uac00 \uae30\ub2a5\uc73c\ub85c\ub294 [\ubbf8\ub300\uc751 \uc544\ubc14\ud0c0\uc758 \uc637\uc744 \uac15\uc81c\ub85c \uc785\ud600\uc8fc\ub294 \uae30\ub2a5](https://arca.live/b/vrchat/131183103)\uc774 \uc788\ub294\ub370 \uc774\uac70\ub294 \ub2e4\ub978 \uac15\uc88c\ub85c \uac08\uc74c\ud55c\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Mesh Settings\\r\\n\\r\\n![Mesh Settings](./modularavatar/Unity_Modular%20Avatar_Mesh%20Settings_1.jpg)\\r\\n![Mesh Settings](./modularavatar/Unity_Modular%20Avatar_Mesh%20Settings_2.jpg)\\r\\n\\r\\n**\ud558\uc704 \uc624\ube0c\uc81d\ud2b8\ub4e4\uc758 \uc575\ucee4 \uc624\ubc84\ub77c\uc774\ub4dc(Anchor Override)\uc640 \ub8e8\ud2b8 \ubcf8(Root Bone), \ubc14\uc6b4\ub4dc\ub97c \uc77c\uad04\ub85c \ud1b5\uc77c**\uc2dc\ucf1c \uc900\ub2e4\\r\\n\\r\\n\uc575\ucee4 \uc624\ubc84\ub77c\uc774\ub4dc\ub791 \ubc14\uc6b4\ub4dc \uc138\ud305\uc744 \uc548 \ud558\uba74 \ube5b \ubc1b\ub294 \uac15\ub3c4 \ub2ec\ub77c\uc9c0\uace0, \uac11\uc790\uae30 \uc624\ube0c\uc81d\ud2b8\uac00 \uc0ac\ub77c\uc9c0\ub294 \ubb38\uc81c\uac00 \uc0dd\uae30\ubbc0\ub85c \uc544\ub798\uc758 \ub0b4\uc6a9\uc744 \uadf8\ub300\ub85c \uc0ac\uc6a9\ud558\uba74 \ubcf4\ud1b5 \ubb38\uc81c\uac00 \uc5c6\ub2e4\\r\\n\\r\\n\uc0ac\uc6a9 \ubc29\ubc95\uc740 **\uc124\uc815 \ubc0f \uc0c1\uc18d\uc73c\ub85c \ud558\uace0, \uc2e4\uc81c \uc544\ubc14\ud0c0\uc758 \uc575\ucee4 \uc624\ubc84\ub77c\uc774\ub4dc\uac00 \ubb34\uc5c7\uc778\uc9c0 \ucc3e\uc544\uc11c \uadf8\uac78 \ub123\uc5b4\uc8fc\uba74 \ub428, \ub8e8\ud2b8 \ubcf8\uc740 \ubcf4\ud1b5 \uc544\ubc14\ud0c0 Hips \ubcf8 \ub123\uc73c\uba74 \ub418\uace0, \ubc14\uc6b4\ub4dc\ub294 \uc13c\ud130 (0, 0, 0), \ud06c\uae30 (1, 1, 1)\ub85c** \ub123\uc5b4\uc8fc\uba74 \ub428\\r\\n\\r\\nSPS \ud50c\ub7ec\uadf8 \uae30\ub2a5 \uc788\ub294 \uba54\uc26c \uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \ub8e8\ud2b8 \ubcf8 \uc124\uc815\uc744 \uc548 \ud574\uc57c \ub418\ub294\uac70 \uac19\uc74c\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Move Independently\\r\\n\\r\\n![Move Independently](./modularavatar/Unity_Modular%20Avatar_Move%20Independently.jpg)\\r\\n\\r\\n\uac04\ub2e8\ud558\uac8c **\uc790\uc2dd \ubcf8\uc5d0 \uc601\ud5a5\uc744 \uc548 \ubbf8\uce58\uace0 \ud574\ub2f9 \ubcf8\ub9cc \uc870\uc791**\ud560 \uc218 \uc788\uac8c \ud574 \uc90c\\r\\n\\r\\n\uc774\uac70\ub294 \uc8fc\ub85c \uc804\uc6a9 \uc637\uc774 \uc544\ub2cc \uac78 \uc785\ud790 \ub54c \ub9ce\uc774 \uc4f8 \ud150\ub370, \uc0c1\uc704 \ubcf8\uc744 \uac74\ub4dc\ub9ac\uba74 \ud558\uc704 \ubcf8\ub3c4 \ud568\uaed8 \uc6c0\uc9c1\uc5ec\uc838\uc11c \uc5ec\ub7ec \ubc88 \uc870\uc815\ud574\uc57c \ub418\ub294\ub370 \uadf8\ub7f0 \uac78 \ud574\uacb0 \ud574 \uc90c\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Parameters\\r\\n\\r\\n![Parameters](./modularavatar/Unity_Modular%20Avatar_Parameters_1.jpg)\\r\\n![Parameters](./modularavatar/Unity_Modular%20Avatar_Parameters_2.jpg)\\r\\n\\r\\n**\uc544\ubc14\ud0c0 \ud30c\ub77c\uba54\ud130\uc5d0 \uae30\ubbf9\uc6a9 \ud30c\ub77c\uba54\ud130\ub97c \ub123\uc744 \ub54c \uc0ac\uc6a9**\ud568\\r\\n\\r\\n\uc0ac\uc6a9 \ubc29\ubc95\uc740 \uc9c1\uc811 \ud30c\ub77c\uba54\ud130\ub4e4\uc744 \ub123\uc5b4\ub3c4 \ub418\uae34 \ud558\ub294\ub370, \uae30\ubbf9 \uc6a9\ub3c4\ub85c **\uc804\uc6a9 \ud30c\ub77c\uba54\ud130 \ud30c\uc77c\uc744 \ub9cc\ub4e4\uace0 \uc774 \uc5d0\uc14b\uc744 \ub123\uc5b4\uc8fc\uba74 \ub4f1\ub85d \uc548 \ub41c \ud30c\ub77c\uba54\ud130\ub4e4\uc744 \uc544\ub798\uc5d0 \ubcf4\uc5ec\uc8fc\ub294\ub370 \uadf8\uac78 \ub20c\ub7ec\uc8fc\uba74 \ub4f1\ub85d**\uc774 \ub41c\ub2e4, \uadf8\ub300\ub85c \uc368\ub3c4 \ub418\uc9c0\ub9cc \uac1c\uc778\uc801\uc73c\ub85c \uc880 \ub354 \uc815\ud655\ud558\uac8c \ud30c\ub77c\uba54\ud130 \ud0c0\uc785\uc774\ub791 \uae30\ubcf8\uac12\uc744 \ud55c \ubc88 \ub354 \uc785\ub825\ud574 \uc8fc\ub294 \ud3b8\\r\\n\\r\\n\uc5ec\uae30\uc11c \ub204\ub77d \ub418\uba74 \uba54\ub274 \ubc84\ud2bc\uc774\ub098 \uae30\ubbf9\uc774 \uc791\ub3d9 \uc548 \ud558\ub2c8 \ub204\ub77d\uc774 \uc5c6\ub294\uc9c0 \uaf2d \ud655\uc778\ud558\uc790\\r\\n\\r\\n:::info\\r\\n\\r\\n\ud30c\ub77c\uba54\ud130\ub97c ``/``\ub85c \uad6c\ubd84\ud558\uba74 \uacc4\uce35\uc774 \uc0dd\uaca8\uc11c \ud30c\ub77c\uba54\ud130\uac00 \ub9ce\uc544\uc838\ub3c4 \ucc3e\uae30\uac00 \uc26c\uc6cc\uc9c4\ub2e4, \uc608\uc2dc\ub97c \uae30\uc900\uc73c\ub85c \ud30c\ub77c\uba54\ud130\ub97c \ud638\ucd9c\ud558\uba74 Moe, Cloth, CityChic 3\uac1c\uc758 \ubb36\uc74c\uc774 \uc0dd\uaca8\uc11c \uc9c0\uc815\ud558\uae30 \uc26c\uc6cc\uc9d0\\r\\n\\r\\n:::\\r\\n\\r\\n<br/>\\r\\n\\r\\n### Shape Changer\\r\\n\\r\\n![Shape Changer](./modularavatar/Unity_Modular%20Avatar_Shape%20Changer_1.jpg)\\r\\n![Shape Changer](./modularavatar/Unity_Modular%20Avatar_Shape%20Changer_2.jpg)\\r\\n\\r\\n\uac04\ub2e8\ud788 **\ucef4\ud3ec\ub10c\ud2b8\uac00 \uc138\ud305\ub41c \uc624\ube0c\uc81d\ud2b8\ub97c \ucf1c\uba74 \ub2e4\ub978 \uc624\ube0c\uc81d\ud2b8\ub4e4\uc758 \uc250\uc774\ud504\ud0a4 \uc218\uce58\ub97c \uc785\ub825\ud55c\ub300\ub85c \ubc14\uafd4\uc8fc\uace0, \uaebc\uc9c0\uba74 \ubc18\ub300\ub85c \uc6d0\ub798\ub300\ub85c \ub3cc\uc544\uac00\ub294 \uae30\ub2a5**\uc744 \ud55c\ub2e4\\r\\n\\r\\n\uc608\ub97c \ub4e4\uba74 \uc2a4\ud0c0\ud0b9\uc744 \ucf1c\uba74 \ubc14\ub514\uc758 \uc2a4\ud0c0\ud0b9 \uc250\uc774\ud504\ud0a4\ub97c 100\uc73c\ub85c \ub9cc\ub4e4 \uc218 \uc788\ub2e4, \ub610\ud55c \uc2a4\ud0c0\ud0b9 \uc885\ub958\uac00 2\uac1c \uc774\uc0c1\uc774\ub354\ub77c\ub3c4 \ub098\ub984\ub300\ub85c \uc54c\uc544\uc11c \uc218\uce58\ub97c \uc870\uc808\ud574 \uc900\ub2e4\\r\\n\\r\\n\ud2b9\uc774\ud558\uac8c \uc0ad\uc81c\ub77c\ub294 \uc250\uc774\ud504\ud0a4 \uc635\uc158\uc774 \uc788\ub294\ub370, \uc250\uc774\ud504\ud0a4\uac00 \uac74\ub4dc\ub9ac\ub294 \ubc84\ud14d\uc2a4\ub4e4\uc744 \uc0ad\uc81c\ud558\uc5ec \ucd5c\uc801\ud654 \ud558\ub294 \uae30\ub2a5\uc774 \uc874\uc7ac\ud55c\ub2e4, \uc774\uac70\ub294 ``\uc2a4\ud0c0\ud0b9\uc744 \ud56d\uc0c1 \uc2e0\uace0 \uc788\uc73c\ub2c8\uae4c \ubc1c \uba54\uc26c\ub97c \uc544\uc608 \uc9c0\uc6cc \uc8fc\uc138\uc694`` \uac19\uc774 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub2e4, \ub530\ub77c\uc11c \uc26c\ub9c1\ud06c(Shrink) \uc250\uc774\ud504\ud0a4\uc640 \uac19\uc774 \uc0ac\uc6a9\ud558\ub294 \uac78 \uc0c1\uc815\ud558\uace0 \ub9cc\ub4e4\uc5b4\uc9c4 \uae30\ub2a5\uc774\ub2e4\\r\\n\\r\\n\uc774 \ucef4\ud3ec\ub10c\ud2b8\ub294 Material Setter, Object Toggle \uc774\ub791 \ubb36\uc5b4\uc11c \ubd10\ub3c4 \ub418\ub294 \ub0b4\uc6a9\uc778\ub370, Material Setter\ub294 \ucf1c\uc9c0\uba74 \ub2e4\ub978 \uc624\ube0c\uc81d\ud2b8\ub4e4\uc758 \uba38\ud14c\ub9ac\uc5bc\uc744 \uc9c0\uc815\ud55c \uac78\ub85c \ubc14\uafd4\uc8fc\uace0, Object Toggle\uc740 \ucf1c\uc9c0\uba74 \ub2e4\ub978 \uc624\ube0c\uc81d\ud2b8\ub4e4\uc758 On/Off\ub97c \ubc14\uafc0 \uc218 \uc788\ub2e4\\r\\n\\r\\nObject Toggle\uc740 \ud53c\uc9c1\uc2a4\ubcf8 \uc624\ube0c\uc81d\ud2b8\ub97c \ucf1c\uba74 \ubcf8\ub4e4\uc774 \ucf1c\uc9c0\uace0, \ub044\uba74 \ubcf8\uc744 \ube44\ud65c\uc131\ud654 \ud558\ub294 \ubc29\uc2dd\uc73c\ub85c \uc751\uc6a9\uc774 \uac00\ub2a5\ud560 \ub4ef\\r\\n\\r\\n\uc774\ub7ec\ud55c \ucef4\ud3ec\ub10c\ud2b8\ub4e4\uc744 \ub9ac\uc561\ud2f0\ube0c \ucef4\ud3ec\ub10c\ud2b8\ub77c\uace0 \ud558\ub294\ub370, \uc774\ub7ec\ud55c \ud615\uc2dd\uc740 **\uaebc\uc9c0\uba74 \uc791\ub3d9\ud558\ub294 \ubc29\uc2dd\uc73c\ub85c \uc870\uac74\uc744 \ubc18\ub300\ub85c \uc124\uc815 \ud560 \uc218\ub3c4 \uc788\ub2e4**, \ub610\ud55c \uc81c\ub300\ub85c \uc791\ub3d9\ud558\ub294\uc9c0 \ud14c\uc2a4\ud2b8 \ud560 \uc218 \uc788\ub294 \uc870\uac74\uc744 \uac15\uc81c\ub85c \uc801\uc6a9\ud574 \ubcf4\ub294 \ub514\ubc84\uadf8 \uae30\ub2a5\ub3c4 \uc788\uc73c\ub2c8 \uc791\uc131\ud560 \ub54c \ucc38\uace0\ud558\uba74 \uc88b\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n### World Fixed Object\\r\\n\\r\\n![World Fixed Object](./modularavatar/Unity_Modular%20Avatar_World%20Fixed%20Object.jpg)\\r\\n\\r\\n**\uc6d4\ub4dc\uc5d0 \uc624\ube0c\uc81d\ud2b8\ub97c \uace0\uc815**\ud560 \ub54c \uc0ac\uc6a9\ud55c\ub2e4\\r\\n\\r\\n\ud574\ub2f9 \ucef4\ud3ec\ub10c\ud2b8\uac00 \ub4e4\uc5b4\uac04 \uc624\ube0c\uc81d\ud2b8\ub294 \uc5ec\ub7ec \uac1c\uac00 \uc788\uc5b4\ub3c4 \uc77c\uad04\ub85c \uc6d4\ub4dc \uace0\uc815\uc6a9 \uac8c\uc784 \uc624\ube0c\uc81d\ud2b8\uc5d0 \uc0c1\uc18d\uc2dc\ud0a4\uace0 \uc544\ubc14\ud0c0\uc758 (0, 0, 0) \uc704\uce58\uc5d0 \uace0\uc815\uc2dc\ucf1c \uc8fc\ub294\ub370, \ubcf4\ud1b5 **\ucee8\uc2a4\ud2b8\ub808\uc778\ud2b8(Constraint; \uc81c\uc57d)\ub85c \uc190\uc774\ub098 \uc544\ubc14\ud0c0\ub97c \ub530\ub77c\ub2e4\ub2c8\ub2e4\uac00 \ucee8\uc2a4\ud2b8\ub808\uc778\ud2b8\ub97c \ube44\ud65c\uc131\ud654\ud558\uba74 \uc6d0\ud558\ub294 \uacf3\uc5d0 \uace0\uc815**\ud558\ub294 \ubc29\uc2dd\uc73c\ub85c \uc6d4\ub4dc \uace0\uc815 \uae30\ub2a5\uc744 \uc190\uc27d\uac8c \ub9cc\ub4e4 \uc218 \uc788\uc74c\\r\\n\\r\\n:::warning\\r\\n\\r\\n\ud504\ub808\uc784 \ucc98\ub9ac \ub9c8\uc9c0\ub9c9\uc5d0 \ucee8\uc2a4\ud2b8\ub808\uc778\ud2b8\uac00 \uc801\uc6a9\ub418\ubbc0\ub85c **\ub208\uc73c\ub85c \ubcf4\ub294 \uc704\uce58\uc640 \ucee8\ud14d\ud2b8(Contact)\uac00 \uc791\ub3d9\ud558\ub294 \uc704\uce58\uac00 \ubd88\uc77c\uce58\ud558\uac8c \ub290\ub9ac\uac8c \uc5c5\ub370\uc774\ud2b8** \ub418\ubbc0\ub85c, \uc774 \ubd80\ubd84\uc740 \ud604\uc7ac [VRChat\uc758 \ud55c\uacc4\uc810](https://ask.vrchat.com/t/developer-update-19-september-2024/26829#p-56092-physbone-constraint-execution-order-6)\uc774\ub2e4\\r\\n\\r\\n:::\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ud30c\ub77c\uba54\ud130 \uc0ac\uc6a9\ub7c9 \ud655\uc778\\r\\n\\r\\n![Information](./modularavatar/Unity_Modular%20Avatar_Information.jpg)\\r\\n\\r\\n\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\ub85c \uc637\uc774\ub098 \uae30\ubbf9\uc744 \ub123\ub2e4\ubcf4\uba74 **\uc5c5\ub85c\ub4dc \ud560 \ub54c Avatar validation failed\ub77c\ub294 \uc5d0\ub7ec**\uac00 \ubc1c\uc0dd\ud560 \ub54c\uac00 \uc788\ub294\ub370, \uc5ec\ub7ec\uac00\uc9c0 \uc6d0\uc778\uc774 \uc788\uaca0\uc9c0\ub9cc **\ubcf4\ud1b5 \ud30c\ub77c\uba54\ud130 \ucd08\uacfc**\ub85c \uc0dd\uae30\ub294 \uacbd\uc6b0\uac00 \ub9ce\ub2e4, \ud655\uc778\ud558\ub824\uba74 ``Tools > Modular Avatar > Show Modular Avatar Information`` \uc5d0 \ub4e4\uc5b4\uac00\uba74 \ud604\uc7ac \uc544\ubc14\ud0c0 \ud30c\ub77c\uba54\ud130\uac00 \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\ub97c \ud3ec\ud568\ud574\uc11c \uc5bc\ub9c8\ub098 \uc0ac\uc6a9\ud558\uace0 \uc788\ub294\uc9c0 \ubcf4\uc5ec\uc900\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \uc544\ubc14\ud0c0 \ubc0f \uc758\uc0c1 \uc81c\uc791\uc790\ub97c \uc704\ud55c \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \ub0b4\uc6a9\\r\\n\\r\\n\uc544\ubb34\ub798\ub3c4 \uc544\ubc14\ud0c0 \uc81c\uc791\uc790\ubcf4\ub2e4\ub294 \uc758\uc0c1\uc774\ub098 \uae30\ubbf9 \uc81c\uc791\uc790\ub4e4\uc774 \uc54c\uc544\uc57c \ud558\ub294 \ub0b4\uc6a9\uc778\ub370, \uc704\uc758 \uad6c\uc131\uc694\uc18c\uac00 \ub204\ub77d\uc774 \ub418\uc5b4\uc11c \uc9c1\uc811 \uc138\ud305\ud574\uc57c \ub418\ub294 \uacbd\uc6b0\ub3c4 \uc885\uc885 \uc788\uc5b4\uc11c \uc774\ub7f0 \ub0b4\uc6a9\ub4e4\uc774 \uc81c\ub300\ub85c \ub4e4\uc5b4\uac00\uba74 \ub354 \uac04\ub2e8\ud558\uac8c \uc544\uc774\ud15c \uc138\ud305 \ud560 \uc218 \uc788\uc73c\ub2c8 \uace0\uac1d \ub9cc\uc871\ub3c4\uac00 \ub354 \ub192\uc544\uc9c8 \uac70\uc784\\r\\n\\r\\n\uac1c\uc778\uc801\uc73c\ub85c \uc758\uc0c1\uc758 \uc77c\ubd80\ub97c \uaed0\ub2e4 \ucf30\ub2e4 \ud558\ub294 \uac78 \ub9ce\uc774 \ud558\ub294\ub370 \uc774\ub7f0 \uae30\ub2a5\ub3c4 \ubbf8\ub9ac \uc138\ud305\ud574\uc11c \ub4e4\uc5b4\uac00 \uc788\uc73c\uba74 \uc88b\uaca0\uc74c, **\uc138\ud305 \ub41c \ub0b4\uc6a9\uc744 \uc720\uc800\ub4e4\uc774 \ube7c\ub294\uac70\ub294 \uac04\ub2e8\ud558\uc9c0\ub9cc, \uc9c1\uc811 \uc138\ud305\ud558\ub294\uac8c \uc0c1\ub300\uc801\uc73c\ub85c \ub354 \uc5b4\ub824\uc6b0\ubbc0\ub85c** \uc81c\uc791\uc790\ub4e4\uc774 \uc870\uae08 \ub354 \uc2e0\uacbd \uc4f0\uba74 \uace0\uac1d\uc774 \ud3b8\ud574\uc9c4\ub2e4 \uc0dd\uac01\ud568\\r\\n\\r\\n\uc544\uc6b8\ub7ec Variant Prefab\uc774 \uc548 \ub418\uc5b4\uc788\ub2e4\ub358\uac00 Prefab \uad6c\uc870\uac00 \uc81c\ub300\ub85c \uc548 \ub418\uc5b4\uc788\ub294 \uacbd\uc6b0\ub97c \ub9ce\uc774 \ubcf4\ub294\ub370, \uc774\ub7f0 \uc88b\uc740 \uae30\ub2a5\ub4e4\uc744 \uc798 \ud65c\uc6a9\ud574 \uc92c\uc73c\uba74 \uc88b\uaca0\ub294\ub370 \ub77c\uace0 \uc0dd\uac01\uc744 \uc5c4\uccad \ub9ce\uc774 \ud568\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Path](./modularavatar/Unity_Modular%20Avatar_Prefab%20Path.jpg)\\r\\n \\r\\n\uac01 \ucef4\ud3ec\ub10c\ud2b8\ub4e4\uc758 \uacbd\ub85c\ub4e4\uc740 Prefab\uc5d0\uc11c \uc9c1\uc811 \uc218\uc815\uc740 \uc548 \ub418\ub294\ub370, **\uc544\ubc14\ud0c0\uc5d0 \uc124\uc815\ud558\uace0 Prefab\uc5d0 \uc801\uc6a9**\ud558\uba74 \ub428, \uc77c\ubd80 \uc544\ubc14\ud0c0\ub294 \ub8e8\ud2b8 \ubcf8\uacfc \uc544\ub9c8\ud29c\uc5b4 \uc774\ub984\uc774 \uaddc\uaca9 \uc678\uc778 \uacbd\uc6b0\ub3c4 \uc788\uc5b4\uc11c \ucd94\uac00\ub85c \ud655\uc778\ud574\uc57c \ud558\uace0, \uc575\ucee4 \uc624\ubc84\ub77c\uc774\ub4dc\ub3c4 \uc544\ubc14\ud0c0\ub9c8\ub2e4 \ub2e4 \ub2e4\ub974\ub2c8 \uc774\uac78 \ub123\uc5b4\uc8fc\ub294\uac8c \ub178\uac00\ub2e4\uc784\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ud6c4\uae30\\r\\n\\r\\n\uc9c0\uae08\uc758 BOOTH \uc2dc\uc7a5\uc774 \uc5c4\uccad \uc131\uc7a5\ud55c \uc774\uc720 \uc911\uc5d0 \ud558\ub098\uac00 \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\ub3c4 \uc5b4\ub290 \uc815\ub3c4 \uc5ed\ud560\uc744 \ud55c\ub2e4 \uc0dd\uac01\ud568, \uc758\uc0c1\uc774\ub098 \uae30\ubbf9\uc744 \ub123\uace0 \uc801\uc6a9\ud558\uae30 \uc26c\uc6b0\ub2c8\uae4c \ud50c\ub808\uc774\uc5b4\ub4e4\ub3c4 \ubd80\ub2f4 \uc5c6\uc774 \uad6c\ub9e4 \ubc0f \uc801\uc6a9\ud558\uace0 \ub354 \uc7ac\ubbf8\uc788\ub294 \uc77c\uc5d0\ub9cc \uc9d1\uc911 \ud560 \uc218 \uc788\uc73c\ub2c8\uae4c\\r\\n\\r\\n\uadf8\ub9ac\uace0 \ubd80\uc2a4\uc5d0\uc11c \uc758\uc0c1 \uc0c0\ub294\ub370 \uc704\uc758 \ub0b4\uc6a9\ub4e4\uc774 \uc548 \ub418\uc5b4\uc788\uc73c\uba74 \ub098\uba38\uc9c0 \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uc791\uc5c5\ud574\uc57c \ub418\ub2c8\uae4c \uc774\uac8c \ubc88\uac70\ub85c\uc6c0, \ub610\ud55c \uc5c4\uc5f0\ud788 \ub313\uac00\ub97c \uc9c0\ubd88\ud558\uace0 \uad6c\ub9e4\ud55c \uac74\ub370 \uae54\ub054\ud558\uac8c \ud55c \ubc88\uc5d0 \ub4e4\uc5b4\uac00\ub294 \uc758\uc0c1\uc744 \ud76c\ub9dd\ud558\ub294\uac74 \uc790\uc5f0\uc2a4\ub7ec\uc6b4\uac70\ub77c \ubd04\\r\\n\\r\\n\uc704\uc758 \ub0b4\uc6a9\ub4e4\uc744 \uc774\ud574\ud558\uace0 \uc5d0\uc14b\uc744 \ub9cc\ub4e4\uc5b4\uc11c \ud30c\ub294\uac8c \ubd80\uc2a4 \uc544\uc774\ud15c \uc81c\uc791\uc774\ubbc0\ub85c, \uc5ec\ub7ec\ubd84\ub4e4\ub3c4 \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\ub85c \uc7ac\ubbf8\uc788\ub294 \uc544\uc774\ud15c\uc744 \ub9cc\ub4e4\uc5b4\uc11c \ubd80\uc2a4\uc5d0 \ud310\ub9e4\ud574 \ubcf4\uc790"},{"id":"vrchat_avatar_component_guide","metadata":{"permalink":"/ja/blog/vrchat_avatar_component_guide","source":"@site/blog/2025-04-13-guide-component.md","title":"VRChat \uc544\ubc14\ud0c0 \uae30\ubcf8 \uad6c\uc870\ub3c4","description":"Component","date":"2025-04-13T00:00:00.000Z","tags":[{"inline":false,"label":"VRChat","permalink":"/ja/blog/tags/vrchat","description":"VRChat \uad00\ub828 \uc544\ud2f0\ud074"},{"inline":false,"label":"\uac15\uc88c","permalink":"/ja/blog/tags/guide","description":"VRChat \uad00\ub828 \uac15\uc88c"},{"inline":false,"label":"Unity","permalink":"/ja/blog/tags/unity","description":"Unity \uad00\ub828 \uc544\ud2f0\ud074"}],"hasTruncateMarker":false,"authors":[{"name":"\ub9c8\ub07c\uc544\ub610","title":"VRSuya \ubaa8\ub378\ub7ec","url":"https://twitter.com/VRC_Macchiato","imageURL":"https://github.com/crestudio.png","key":"macchiato","page":null}],"frontMatter":{"slug":"vrchat_avatar_component_guide","title":"VRChat \uc544\ubc14\ud0c0 \uae30\ubcf8 \uad6c\uc870\ub3c4","authors":"macchiato","tags":["vrchat","guide","unity"]},"unlisted":false,"prevItem":{"title":"\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0 \uac00\uc774\ub4dc","permalink":"/ja/blog/modular_avatar_guide"},"nextItem":{"title":"Unity Prefab \uac00\uc774\ub4dc","permalink":"/ja/blog/unity_prefab_guide"}},"content":"![Component](./component/Unity_Component.jpg)\\r\\n\\r\\nVRChat \uc544\ubc14\ud0c0\ub97c \ub9cc\ub4e4 \ub54c \uc790\uc8fc \uc4f0\uc774\ub294 \ucef4\ud3ec\ub10c\ud2b8\ub4e4\uc744 \uc804\uccb4 \uac1c\uc694\uc640 \ud750\ub984\uc744 \ubcfc \uc218 \uc788\uac8c \ub9cc\ub4e4\uc5c8\uc74c\\r\\n\\r\\n\ucc98\uc74c\uc73c\ub85c Unity\ub85c \uc544\ubc14\ud0c0\ub97c \uac1c\ubcc0\ud560 \ub54c\uc5d0\ub294 **\uc5b4\ub514\ub97c \uc218\uc815\ud574\uc57c \ub420 \uc9c0 \ud30c\uc545**\ud558\ub294\uac8c \uc27d\uc9c0 \uc54a\uc740\ub370, \uc704\uc758 \uac1c\uc694\ub3c4\ub97c \ucc38\uace0\ud558\uba74 \uc5b4\ub514\ub97c \uc218\uc815\ud574\uc57c \ub41c\ub2e4\ub358\uac00 \uad81\uae08\ud55c \uc810\uc744 \ucc3e\uc544 \ubcfc \ub54c \ub3c4\uc6c0\uc774 \ub420 \uac70\ub77c \uc0dd\uac01\ud568"},{"id":"unity_prefab_guide","metadata":{"permalink":"/ja/blog/unity_prefab_guide","source":"@site/blog/2025-04-07-guide-prefab.md","title":"Unity Prefab \uac00\uc774\ub4dc","description":"\uc694\uc57d","date":"2025-04-07T00:00:00.000Z","tags":[{"inline":false,"label":"VRChat","permalink":"/ja/blog/tags/vrchat","description":"VRChat \uad00\ub828 \uc544\ud2f0\ud074"},{"inline":false,"label":"\uac15\uc88c","permalink":"/ja/blog/tags/guide","description":"VRChat \uad00\ub828 \uac15\uc88c"},{"inline":false,"label":"Unity","permalink":"/ja/blog/tags/unity","description":"Unity \uad00\ub828 \uc544\ud2f0\ud074"}],"hasTruncateMarker":false,"authors":[{"name":"\ub9c8\ub07c\uc544\ub610","title":"VRSuya \ubaa8\ub378\ub7ec","url":"https://twitter.com/VRC_Macchiato","imageURL":"https://github.com/crestudio.png","key":"macchiato","page":null}],"frontMatter":{"slug":"unity_prefab_guide","title":"Unity Prefab \uac00\uc774\ub4dc","authors":"macchiato","tags":["vrchat","guide","unity"]},"unlisted":false,"prevItem":{"title":"VRChat \uc544\ubc14\ud0c0 \uae30\ubcf8 \uad6c\uc870\ub3c4","permalink":"/ja/blog/vrchat_avatar_component_guide"},"nextItem":{"title":"\ube14\ub85c\uadf8","permalink":"/ja/blog/intro"}},"content":"## \uc694\uc57d\\r\\n\\r\\n1. Variant Prefab \uae30\ub2a5\uc744 \uc798 \ud65c\uc6a9\ud558\uc790\\r\\n1. Prefab\uc744 \uc798 \ud65c\uc6a9\ud558\uba74 \uc81c\uc791\uc790 \ubfd0\ub9cc \uc544\ub2c8\ub77c \uc18c\ube44\uc790\ub4e4\ub3c4 \uc720\uc6a9\ud558\ub2e4\\r\\n1. \uadf8\ub807\uc9c0 \uc54a\uc73c\uba74 \ub178\uac00\ub2e4\ud574\uc11c \uc815\uc0c1\ud654\ud574\uc57c \ud55c\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \uc11c\ub860\\r\\n\\r\\n\uc61b\ub0a0 VRChat\uc740 **\uc544\ubc14\ud0c0\ub97c \uad6c\ub9e4\ud574\ub3c4 \ubaa8\ub378\uc774\ub791 \ud14d\uc2a4\uccd0 \ud30c\uc77c\ub9cc \ub2ec\ub791 \ub4e4\uc5b4 \uc788\uc5c8\ub358 \uc2dc\uc808**\uc774 \uc788\uc5c8\ub2e4, \uadf8 \ub54c \ub2f9\uc2dc VRChat \uc720\uc800\ub77c\uba74 \uba38\ud14c\ub9ac\uc5bc\uc774\ub098 \ub2e4\uc774\ub098\ubbf9 \ubcf8, \ud45c\uc815 \uc560\ub2c8\uba54\uc774\uc158 \uc81c\uc791, VRChat \uc544\ubc14\ud0c0 \uc138\ud305\uc744 \ubaa8\ub450 \uc9c1\uc811 \ud560 \uc904 \uc54c\uc544\uc57c \uc0b4 \uc218 \uc788\uc5c8\ub358 \uc815\ub9d0 \uc57c\uc0dd\uc758 \uc2dc\ub300\uc600\ub2e4, \ud558\uc9c0\ub9cc \uc2dc\uac04\uc774 \uc5b4\ub290\ub367 \ud758\ub7ec \ub9ce\uc740 \uc81c\uc791\uc790\ub4e4\uc774 \uc544\ubc14\ud0c0 \uc138\ud305\uc744 \ubbf8\ub9ac \ud574 \uc8fc\uace0 \uae30\ubbf9\uc774 \uc788\ub294 \uc758\uc0c1\uc774\ub77c\ub3c4 \uc774\uc81c \ub123\uae30\ub9cc \ud574\ub3c4 \uc785\ud790 \uc218 \uc788\ub294 \uc2dc\ub300\uac00 \ub3c4\ub798\ud588\uc73c\ub2c8, \uc774\ub7ec\ud55c \ubaa8\ub4e0 \uac83\ub4e4\uc774 \uac00\ub2a5\ud574\uc9c4 \uc774\uc720\uac00 \ubc14\ub85c **\ud504\ub9ac\ud339(Prefab)** \uc774\ub77c\ub294 \uac1c\ub150\uc774 \uc874\uc7ac\ud558\uae30 \ub54c\ubb38\uc774\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ud504\ub9ac\ud339(Prefab) \uac1c\ub150\\r\\n\\r\\n\uac04\ub2e8\ud558\uac8c \ud45c\ud604\ud558\uc790\uba74 **\ud15c\ud50c\ub9bf**\uc774\ub791 \ube44\uc2b7\ud55c \uac70\ub77c\uace0 \ubcf4\uba74 \ub41c\ub2e4, \uc544\ubc14\ud0c0\ub97c \uc0c0\ub294\ub370 VRChat \uc544\ubc14\ud0c0 \uc138\ud305\ud558\uace0 \ud53c\uc9c1\uc2a4\ubcf8 \uc138\ud305\ud574\uc57c \ub418\uace0, \uc637\uc744 \uc0c0\ub294\ub370 \uae30\ubbf9 \uc138\ud305\ud574\uc57c \ub418\ub294 \uc774\ub7f0 \uc0c1\ud669\uc744 \ub9e4\ubc88 \ud558\uace0 \uc2f6\uc5b4\ud558\ub294 \uc0ac\ub78c\uc740 \uc5c6\uc744 \uac83\uc774\ub2e4, \uc544\ubc14\ud0c0 **\uc138\ud305 \ub2e4 \ub418\uc5b4\uc788\ub294 \uc644\uc131\ud488**\uc774 \uc788\uc73c\uba74 \ub2f9\uc5f0\ud788 \ud3b8\ud560 \uac83\uc774\ub2e4, \uc774\ub7ec\ud55c \uc5ed\ud560\uc744 \ud574 \uc8fc\ub294 \uac8c \ubc14\ub85c \ud504\ub9ac\ud339\uc774\ub2e4\\r\\n\\r\\n\uc720\ub2c8\ud2f0\uc5d0\uc11c \uc9c1\uc811 \ub290\uaef4\ubcf4\uace0 \uc2f6\ub2e4\uba74 \uc544\ubc14\ud0c0 \ubaa8\ub378 \uc6d0\ubcf8 \ud30c\uc77c\uc744 \uc52c\uc5d0\ub2e4\uac00 \uac16\ub2e4 \ub193\uc544\ubcf4\uc790, \uc815\ub9d0 \uc544\ubb34\ub7f0 \uae30\ub2a5\uc774 \uc548 \ub4e4\uc5b4\uc788\uc5b4\uc11c \uba38\ub9ac\uce74\ub77d \ud558\ub098 \uc548 \uc6c0\uc9c1\uc774\ub294 \ubaa8\uc2b5\uc744 \ubcfc \uc218 \uc788\uc9c0\ub9cc, \ud504\ub9ac\ud339\uc73c\ub85c \ud574 \ubcf8\ub2e4\uba74 \uc774\ub7ec\ud55c \uc124\uc815\ub4e4\uc774 \ub2e4 \uc138\ud305\ub418\uc5b4 \uc788\ub2e4\ub294 \ucc28\uc774\uc810\uc744 \uc27d\uac8c \ubc1c\uacac\ud560 \uc218 \uc788\ub2e4\\r\\n\\r\\n\uc544\ub798\uc5d0\ub294 \uc720\ub2c8\ud2f0\uc5d0\uc11c \uc0ac\uc6a9\ub418\ub294 \ud504\ub9ac\ud339\uc758 4\uac00\uc9c0 \uc885\ub958\uc774\ub2e4\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_000.jpg)\\r\\n\\r\\n\uc704\uc758 \ub0b4\uc6a9\ub4e4\uc740 \uac04\ub2e8\ud558\uac8c\ub9cc \uc774\ud574\ud558\uace0 \uc544\ub798 \ub0b4\uc6a9\ub4e4\uc744 \uc77d\uc744 \ub54c \uc774\ub7ec\ud55c \ud504\ub9ac\ud339\ub4e4\uc774 \uc0ac\uc6a9\ub418\ub294\uad6c\ub098 \ud558\uace0 \ub118\uc5b4\uac00\uba74 \ub41c\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_001.jpg)\\r\\n \\r\\n\uc704\uc758 \uc544\ubc14\ud0c0 5\uba85\uc744 \ubcf4\uba74 \uac70\uc758 \ub2e4 \ub611\uac19\uc740\ub370 \uc637\ub9cc \uc11c\ub85c \ub2e4\ub974\ub2e4\ub294 \uac70\ub97c \ubcfc \uc218 \uc788\ub2e4, \uc774\uac70\ub97c \uad6c\ud604\ud558\ub824\uba74 \uba87 \uac00\uc9c0 \ubc29\ubc95\uc774 \uc788\ub2e4\\r\\n\\r\\n- \uccab\ubc88\uc9f8\ub85c\ub294 \ud55c \uc544\ubc14\ud0c0\uc5d0 \uc637\uc744 \ubaa8\ub450 \ub123\uace0 \uc637\uc7a5 \uae30\ub2a5\uc744 \uc4f0\ub294 \ubc29\uc2dd\\r\\n- \ub450\ubc88\uc9f8\ub85c\ub294 \ud55c \uc544\ubc14\ud0c0 \ubca0\uc774\uc2a4\ub85c \uc637\uc744 \uac01\uac01 \ub123\uc740 \ubc14\ub9ac\uc5b8\ud2b8 \ud504\ub9ac\ud339\uc744 \ub9cc\ub4dc\ub294 \ubc29\uc2dd\\r\\n\\r\\n\ub09c\uc774\ub3c4\ub791 \uc811\uadfc\uc131\uc73c\ub85c\ub294 \ub2f9\uc5f0\ud788 \uccab\ubc88\uc9f8\uac00 \ud3b8\ud560 \uac83\uc774\ub2e4, \uc544\ubc14\ud0c0 \ud558\ub098\uc5d0 \uc637\uc744 \ub2e4 \ub123\uace0 \uc637\uc7a5\uc744 \ub9cc\ub4e4\uc5b4\uc11c \uc62c\ub9ac\uba74 VRChat\uc5d0\uc11c \ubc84\ud2bc \ud558\ub098 \ub204\ub974\uba74 \uc637\ub3c4 \ubc14\ub00c\uace0 \ub85c\ub529\ub3c4 \uc5c6\uc73c\ub2c8\uae4c, \uc774\ub807\ub2e4 \ubcf4\ub2c8 \ub300\ub2e4\uc218 \uc720\uc800\ub4e4\uc774 \uc544\ubc14\ud0c0 \ud558\ub098\uc5d0 \uc637\uc744 \ub2e4 \ub123\uc5b4\uc11c \ucd5c\uc801\ud654\uac00 \uc5b4\ub824\uc6cc\uc9c0\uace0 \uc810\uc810 \uac8c\uc784\uc774 \ubb34\uac70\uc6cc\uc9c0\uace0 \uc788\ub2e4, \ud604\uc2e4\ub85c \ube44\uc720\ud558\uc790\uba74 \ub0b4\uac00 \ub2f9\uc7a5 \uc548 \uc785\uace0 \uc788\ub294 \uc637\uc744 \uac00\ubc29\uc5d0 \ub123\uc5b4\ub450\uace0 \ub2e4\ub978 \uc0ac\ub78c\ub4e4\uc5d0\uac8c \uacc4\uc18d \ub4e4\uace0 \uc788\uac8c \ud558\ub294\uac70\ub791 \ube44\uc2b7\ud558\ub2e4\uace0 \ubd10\ub3c4 \ub41c\ub2e4\\r\\n\\r\\n\ub450\ubc88\uc9f8 \ubc29\uc2dd\uc740 \uc5b4\ub5a8\uae4c? \ud569\uccd0 \ub193\uc740\uac78 \uac1c\ubcc4\ub85c \ubc14\uafd4\uc57c \ud558\ub2c8 \uc544\ub798\uc640 \uac19\uc740 \uc810\ub4e4\uc774 \uc788\ub2e4\\r\\n\\r\\n- \uc758\uc0c1\ub9c8\ub2e4 \uc5c5\ub85c\ub4dc\ub97c \ud574\uc57c \ud558\ub2c8, \uc544\ubc14\ud0c0 \uc2ac\ub86f\uc774 \ub298\uc5b4\ub098\uace0 \uc5c5\ub85c\ub4dc \ud569\uc0b0 \uc2dc\uac04\uc774 \uc870\uae08 \uae38\uc5b4\uc9c4\ub2e4\\r\\n- \uc81c\ub300\ub85c \ub41c Prefab \ubc29\uc2dd\uc73c\ub85c \uc124\uacc4 \uc548 \ud574 \ub450\uc5c8\uc73c\uba74 \uad00\ub828 \uc138\ud305\ub4e4\uc744 \ub9de\ucdb0\uc11c \ubc14\uafc0 \ud544\uc694\uc131\uc774 \uc788\ub2e4\\r\\n- \uc6d0\ubcf8\uc744 \ubcf4\uc874\ud558\uace0 \uac1c\ubcc4\ub85c \ud574\uc57c \ub418\ub2c8 \ucd94\uac00\uc801\uc778 \uc790\uc2dd \uc5ed\ud560\uc744 \ud560 \ud30c\uc77c\uc744 \uc0dd\uc131 \ubc0f \uad00\ub9ac\ud574\uc57c \ub41c\ub2e4\\r\\n\\r\\n\ud504\ub9ac\ud339\uc5d0 \ub300\ud55c \uc790\uc138\ud55c \uac1c\ub150\uacfc \uc0ac\uc6a9 \ubc29\ubc95\uc740 \uc544\ub798\uc5d0\uc11c \uc870\uae08 \ub354 \uc124\uba85\ud558\uace0, **\uc758\uc0c1\ub9c8\ub2e4 \uc5c5\ub85c\ub4dc\ud558\uae30 \uadc0\ucc2e\uc740 \ubb38\uc81c\ub294 [Continuous Avatar Uploader](https://github.com/anatawa12/ContinuousAvatarUploader) \ub77c\ub294 \uc560\ub4dc\uc628**\uc744 \uc0ac\uc6a9\ud558\uba74 \ud574\uacb0\uc774 \ub41c\ub2e4, \uc5c5\ub85c\ub4dc \ub20c\ub7ec\ub193\uace0 \uc720\ud22c\ube0c \ubcf4\uace0 \uc788\uc73c\uba74 \ub428\\r\\n\\r\\n\uc790 \uadf8\ub7fc \ud504\ub9ac\ud339\uc5d0 \uc5b4\ub5a4 \uae30\ub2a5\uc774 \uc788\ub294\uc9c0 \uc54c\uc544\ubcf4\uace0 \uc81c\ub300\ub85c \uc0ac\uc6a9 \ubc29\ubc95\uc744 \ubc30\uc6cc\ubcf4\uc790\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \uc6d0\ubcf8 Prefab\uacfc\uc758 \ucc28\uc774\uc810\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_002.jpg)\\r\\n\\r\\n\uba3c\uc800 \ud3b8\ub9ac\ud558\uace0 \uac15\ub825\ud55c \uae30\ub2a5 \uc911\uc5d0 \ud558\ub098\uac00 \ubc14\ub85c **\uc6d0\ubcf8\uc744 \uae30\ubc18\uc73c\ub85c \ubb34\uc5c7\uc774 \ub2ec\ub77c\uc84c\ub294\uc9c0** \uc54c\uae30\uac00 \uc27d\uace0, **\ubcc0\uacbd \uc0ac\ud56d\uc744 \uc801\uc6a9\ud558\uac70\ub098 \ub418\ub3cc\ub9ac\ub294\uac8c \uac04\ud3b8**\ud558\ub2e4\ub294 \uc810\uc774\ub2e4\\r\\n\\r\\n\uc6d0\ubcf8 \uc544\ubc14\ud0c0\ub791 \ube44\uad50\ud588\uc744 \ub54c, \uac00\uc2b4\uc774 \ucee4\uc9c0\uace0 \uc624\ud53c\uc2a4\ub8e9\uc744 \uc785\uace0 \uc788\uace0 \uc548\uacbd\uc744 \uc4f0\uace0 \uc788\uace0\u2026 \uc774\ub7ec\ud55c \ubcc0\uacbd \uc0ac\ud56d\ub4e4\uc774 \uc788\ub2e4\ub294 \uac78 \uc27d\uac8c \ud30c\uc545\ud560 \uc218 \uc788\ub2e4\\r\\n\\r\\n\uc774\ub7ec\ud55c Prefab\ub4e4\uc744 \ub9cc\ub4e4\ub2e4 \ubcf4\uba74 \uc774 \ud654\uba74\uc5d0\uc11c \ub0b4\uac00 \uac74\ub4dc\ub9ac\uc9c0 \uc54a\uc558\ub294\ub370 \ubcc0\uacbd\ub418\uc5b4 \uc788\ub294 \uac83\ub4e4\uc774 \uc758\uc678\ub85c \ub9ce\ub2e4\ub294 \uac78 \uc54c\uac8c \ub418\ub294\ub370, \uadf8\ub7f0 \uac78 \uc120\ud0dd\ud574\uc11c \ub418\ub3cc\ub9ac\uace0 \uc6d0\ud558\ub294 \uc0ac\ud56d\ub9cc \ub0a8\uae30\uba74\uc11c \uc804\uccb4\uc801\uc778 \uad6c\uc870\ub97c \ud30c\uc545\ud574 \ub450\uba74 \uc544\ubc14\ud0c0 \uad00\ub9ac\ud558\ub294\uac8c \ud3b8\ud574\uc9c0\ub294 \uac78 \uccb4\uac10\ud560 \uc218 \uc788\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_003.jpg)\\r\\n\\r\\n\ud558\ub098\uc529 \ub20c\ub7ec\ubcf4\uba74 \uc5b4\ub5a0\ud55c \uc810\ub4e4\uc774 \uad6c\uccb4\uc801\uc73c\ub85c \ubc14\ub00c\uc5c8\ub294\uc9c0 \uc67c\ucabd\uacfc \ube44\uad50\ud574\uc11c \ubcfc \uc218 \uc788\ub294\ub370, \uc5ec\uae30\uc5d0\uc11c \ud1b5\uc9f8\ub85c \uc544\ub2c8\uba74 \ube68\uac04 \uc0c1\uc790\ucc98\ub7fc \ubd80\ubd84\ubd80\ubd84\ub9cc \ub610 \ub098\ub220\uc11c \uc561\uc158\uc744 \ucde8\ud560 \uc218 \uc788\ub2e4\\r\\n\\r\\n\uc6d0\ud558\uc9c0 \uc54a\uc73c\uba74 \ub418\ub3cc\ub9ac\uba74 \ub418\uace0, \uc0c1\uc704 \ud504\ub9ac\ud339\uc5d0 \uc801\uc6a9\ud55c\ub2e4\uba74 \ub2e4\ub978 \ubc14\ub9ac\uc5b8\ud2b8 \ud504\ub9ac\ud339\uc5d0\ub3c4 \uc804\ud30c\ud558\uc5ec \uc801\uc6a9 \uc2dc\ud0ac \uc218\ub3c4 \uc788\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## Prefab \ubcc0\uacbd\uc0ac\ud56d \uc801\uc6a9 \ubc0f \ub418\ub3cc\ub9ac\uae30\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_004.jpg)\\r\\n\\r\\n\ud504\ub9ac\ud339 \ub0b4\uc6a9 \uc911\uc5d0\uc11c \uac00\uc7a5 \ud575\uc2ec\uc778 \uae30\ub2a5\ub4e4\uc774\ub2e4\\r\\n\\r\\nPrefab\uc744 \uc52c\uc5d0 \uc62c\ub824\ub193\uace0 \uc218\uc815\ud558\uba74 **\uc606\uc5d0 \ud30c\ub780\uc0c9 \uc904\uc73c\ub85c \ubcc0\uacbd\uc0ac\ud56d\uc774 \uc788\ub2e4\uace0 \ud45c\uc2dc**\uac00 \ub418\ub294\ub370, \uc5ec\uae30\uc11c \uae00\uc528\ub098 \ud5e4\ub354 \ubd80\ubd84\uc744 \uc624\ub978\ucabd \ud074\ub9ad\ud558\uba74 \uad00\ub828 \uba54\ub274\uac00 \ub098\uc628\ub2e4\\r\\n\\r\\n**Apply\ub294 \ubcc0\uacbd\uc0ac\ud56d\uc744 \ud504\ub9ac\ud339\uc5d0 \uc801\uc6a9**\uc744 \uc2dc\ucf1c\uc900\ub2e4, \uc774 \ub54c \uc5b4\ub290 \ud504\ub9ac\ud339\uc5d0 \uc801\uc6a9\uc2dc\ud0a4\ub294\uac8c \ub530\ub77c\uc11c \uacb0\uacfc\uac00 \ub2ec\ub77c\uc9c0\ub2c8 \uc5b4\ub514\uc5d0 \uc801\uc6a9\ud558\ub294\uc9c0 \uc798 \uc77d\uc5b4\ubcf4\uc790\\r\\n\\r\\n**Revert\ub294 \ubcc0\uacbd\uc0ac\ud56d\uc744 \ucde8\uc18c\ud558\uace0 \uc6d0\ubcf8 \ub0b4\uc6a9\uc73c\ub85c** \ub2e4\uc2dc \ubc14\uafd4\uc900\ub2e4\\r\\n\\r\\n\uc5ec\uae30\uc11c \uc911\uc694\ud55c \ud3ec\uc778\ud2b8\ub294 \ud30c\ub780\uc0c9\uc740 **\uc624\ubc84\ub77c\uc774\ub4dc(Override) \uc0c1\ud0dc\ub77c\uace0 \ud558\uc5ec \u2018\ub098\ub294 \uc6d0\ubcf8\uacfc \ub0b4\uc6a9\uc774 \ub2ec\ub77c\uc838\uc57c \ud574\uc694\u2019** \ub77c\ub294 \ub73b\uc774\uae30 \ub54c\ubb38\uc5d0, \uc624\ubc84\ub77c\uc774\ub4dc \uc0c1\ud0dc\uc774\uba74 \uc6d0\ubcf8\uacfc \uac19\uc740 \uac12\uc774\ub354\ub77c\ub3c4 \uc6d0\ubcf8\uc774 \ubc14\ub00c\uc5b4\ub3c4 \ud558\uc704 \uac12\uc740 \ubcc0\ud558\uc9c0 \uc54a\ub294\ub2e4, \ub2e4\uc2dc \ub9d0\ud574 **\uc6d0\ubcf8\uc744 \ub530\ub77c\uac00\uc57c \ud558\ub294 \ub0b4\uc6a9\uc774\ub77c\uba74 Revert\ub97c \ud1b5\ud574 \ub2e4\uc2dc \uc624\ubc84\ub77c\uc774\ub4dc\ub97c \ucde8\uc18c**\ud560 \ud544\uc694\uac00 \uc788\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_005.jpg)\\r\\n\\r\\n\ub3c4\uc2dd\ud654\ub97c \ud558\uba74 \uc704\uc640 \uac19\uc740 \uac1c\ub150\uc73c\ub85c \uc801\uc6a9\uc774 \ub41c\ub2e4, \uc5b4\ub824\uc6b0\uba74 \uc9c1\uc811 \uc720\ub2c8\ud2f0\uc5d0\uc11c \uc624\ube0c\uc81d\ud2b8\ub97c \ub9cc\ub4e4\uc5b4\uc11c \ud14c\uc2a4\ud2b8\ud574 \ubcf4\uba74 \uc774\ud574\uac00 \ub354 \ube60\ub97c \uac83\uc774\ub2e4\\r\\n\\r\\n\ud558\uc704 \ud504\ub9ac\ud339(Variant Prefab)\uc5d0\uc11c \uc0c1\uc704 \ud504\ub9ac\ud339\uc73c\ub85c \uc801\uc6a9\ud558\uba74 **\ud30c\ub780\uc0c9\uc774 \uc5c6\ub294 \uc560\ub4e4\uc5d0\uac8c\ub9cc \uc804\ud30c**\uac00 \ub418\ub294 \ubaa8\uc2b5\uc744 \ubcfc \uc218 \uc788\ub2e4\\r\\n\\r\\n\ub530\ub77c\uc11c \ud504\ub9ac\ud339 \uae30\ub2a5\uc744 \uc798 \ud65c\uc6a9\ud558\ub824\uba74 **\ubcc0\uacbd \uc0ac\ud56d\uc744 \uad00\ub9ac\ud558\uace0 \uc704\uc5d0 \ub4e4\uc5b4\uac08 \ub0b4\uc6a9\uc778\uc9c0 \uc544\ub798\uc5d0 \ub4e4\uc5b4\uac08 \ub0b4\uc6a9\uc778\uc9c0 \uad6c\ubd84\ud558\ub294\uac8c \ud3ec\uc778\ud2b8**\ub77c \ud560 \uc218 \uc788\ub2e4\\r\\n\\r\\n\uc608\ub97c \ub4e4\uba74 \uc0c1\uc704 \ud504\ub9ac\ud339\uc5d0\ub294 AFK\ub77c\ub358\uac00 \uae30\ubbf9\uc774\ub77c\ub358\uac00 \uacf5\ud1b5\uc801\uc73c\ub85c \uc801\uc6a9\ud558\uace0 \uc2f6\uc740 \ub0b4\uc6a9\ub4e4\uc744 \ub123\uc73c\uba74 \ud558\uc704 \ud504\ub9ac\ud339\uc5d0\ub3c4 \uc790\ub3d9\uc73c\ub85c \uc801\uc6a9 \ub418\uc5b4\uc11c \ud3b8\ud560 \uac83\uc774\uace0, \ubc18\ub300\ub85c \ud558\uc704 \ud504\ub9ac\ud339\uc5d0\ub294 \uc637\uc774\ub77c\ub358\uac00 \uc0c9\uc0c1 \uba38\ud14c\ub9ac\uc5bc \uac19\uc774 \uc720\ub2c8\ud06c\ud55c \ubcc0\uacbd \uc0ac\ud56d\ub4e4\uc774 \uc874\uc7ac\ud560 \uac83\uc774\ub2e4\\r\\n\\r\\n\uadf8\ub798\uc11c \uc544\ubc14\ud0c0 \uc637\uc744 \uc785\ud788\ub2e4 \ubcf4\uba74 \ubd80\ubaa8\uac00 \ub418\ub294 \ud504\ub9ac\ud339\uc744 \uc798 \ub9cc\ub4e4\uc5b4\ub450\uba74 \uc0dd\uac01 \uc678\ub85c \ud558\uc704 \ud504\ub9ac\ud339\uc5d0\ub294 \ub123\uc744 \uac8c \uadf8\ub2e4\uc9c0 \uc5c6\uc5b4\uc11c \uae08\ubc29 \ub9cc\ub4e4\uac8c \ub41c\ub2e4, \ub610\ud55c [\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0](https://modular-avatar.nadena.dev/)\uac00 \uc65c \ubaa8\ub4c8\uc778\uc9c0 \uc9c4\uac00\ub97c \ubab8\uc18c \uccb4\uac10 \ud560 \uc218 \uc788\ub2e4, \ub128\ub3c4\ub85c\uc774\ub4dc\ucc98\ub7fc \ud544\uc694\ud55c \ud504\ub9ac\ud339\ub9cc \uc3d9\uc3d9 \ub123\uc73c\uba74 \ubc14\ub85c \uc644\uc131\ub418\ubbc0\ub85c \ud504\ub9ac\ud339\uacfc \ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0\uc758 \uc2dc\ub108\uc9c0\uac00 \uad49\uc7a5\ud558\ub2e4\ub294 \uac78 \uc54c \uc218 \uc788\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ucd94\ucc9c\ud558\ub294 Prefab \uad6c\uc870\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_006.jpg)\\r\\n\\r\\n\uc704\uc758 \ub0b4\uc6a9\ub4e4\uc744 \ubc14\ud0d5\uc73c\ub85c \ucd94\ucc9c\ud558\ub294 \ubc29\uc2dd\uc740 \uc704\uc758 \ub3c4\uc2dd\ud654\ub791 \uac19\uc74c, \uc774\ub7f0 \ub290\ub08c\uc5d0\uc11c \uc6d0\ud55c\ub2e4\uba74 \ub298\ub9ac\uace0 \uc904\uc774\uace0 \ud558\uba74 \ub428\\r\\n\\r\\n\uc774\ub807\uac8c \ud504\ub9ac\ud339\uc73c\ub85c \uad00\ub9ac\ud558\uba74 \uc544\ub798\uc640 \uac19\uc740 \uc810\ub4e4\uc774 \uc788\ub2e4\\r\\n\\r\\n- **\uc544\ubc14\ud0c0\uac00 \uc5c5\ub370\uc774\ud2b8 \ub418\ub354\ub77c\ub3c4 \ub0b4\uac00 \uc218\uc815\ud55c \ubd80\ubd84\uc774 \ub0a0\uc544\uac08 \uc6b0\ub824\uac00 \uc801\uc74c**, \uc774\uac74 \uc544\ubc14\ud0c0 \uc81c\uc791\uc790\ub4e4\ub3c4 \uc5c5\ub370\uc774\ud2b8\ud560 \ub54c \uc774\ub7f0 \uac1c\ub150\uc744 \uc798 \uc774\ud574\ud574\uc57c \ub418\ub294 \ubd80\ubd84\uc774\uae30\ub3c4 \ud568, \uc65c\ub0d0\ud558\uba74 \uc544\ubc14\ud0c0 \uc81c\uc791\uc790\ub294 \ubd80\ubaa8 Prefab \uc5ed\ud560\ub3c4 \ub3d9\uc2dc\uc5d0 \ud558\uae30 \ub54c\ubb38\uc5d0 \uc798 \uad00\ub9ac \ud560 \uc218 \uc788\uc5b4\uc57c \uad6c\ub9e4\uc790\ub4e4\ub3c4 \uc5c5\ub370\uc774\ud2b8 \ud560 \ub54c \ubc1b\uc544\ub4e4\uc774\uae30 \uc27d\uace0 \uc801\uc6a9\ud558\ub294\ub370 \ubd80\ub2f4\uac10\ub3c4 \uc801\uc5b4\uc9d0\\r\\n- **\uc801\uc740 \uc218\uc815\uc73c\ub85c \ubaa8\ub4e0 \uc544\ubc14\ud0c0\uc5d0 \uc801\uc6a9 \uac00\ub2a5**, \uc8fc\ub85c \uae30\ubbf9 \ucabd\uc5d0 \ud574\ub2f9 \ub418\ub294\ub370, \uc544\uc608 \ub2e4\ub978 \uc544\ubc14\ud0c0\ub77c\ub3c4 \ud504\ub9ac\ud339\uc73c\ub85c \uacf5\ud1b5\ub41c \uc694\uc18c\ub97c \uad00\ub9ac\ud588\ub2e4\uba74 \ud55c \ubc88\ub9cc \uc791\uc5c5\ud558\uba74 \uc77c\uad04 \uc801\uc6a9\ub428 \\r\\n- **\uc720\ub2c8\ud2f0 \uc52c \ud30c\uc77c\uc774\ub098 \uc77c\ubd80 \ucd5c\ud558\ub2e8 \ud504\ub9ac\ud339\uc774 \uc190\uc0c1\ub418\uc5b4\ub3c4 \ubb38\uc81c \uc5c6\uc74c**, \ud504\ub9ac\ud339\uc740 \ub530\ub85c \ud30c\uc77c\ub85c \uad00\ub9ac\ub418\uae30 \ub54c\ubb38\uc5d0 \uc52c \ud30c\uc77c\uc774 \ub0a0\uc544\uac00\ub3c4 \ub3c4\uc2dd\ud654\ucc98\ub7fc \uad00\ub9ac \ud588\uc73c\uba74 \uc0ac\uc2e4 \uc0ac\ub77c\uc9c4 \ub0b4\uc6a9\uc774 \uc5c6\uae30\uc5d0 \uae08\ubc29 \ubcf5\uad6c \uac00\ub2a5, \ud558\uc704 \ud504\ub9ac\ud339 \ucabd\ub3c4 \ub2f9\uc5f0\ud788 \uc0c1\ub2e8 \ud504\ub9ac\ud339\uc5d0 \ube44\ud574 \ub4e4\uc5b4\uc788\ub294 \ub0b4\uc6a9\uc774 \uc801\uc73c\ub2c8 \ubcf5\uad6c\ud558\ub294\ub370 \uc2dc\uac04\ub3c4 \uc801\uac8c \uac78\ub9bc\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## Prefab \ub9cc\ub4e4\uae30\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_007.jpg)\\r\\n![Prefab](./prefab/Unity_Prefab_008.jpg)\\r\\n\\r\\n\uc55e\uc5d0\uc11c \ub9ce\uc740 \uc774\uc57c\uae30\ub97c \ud588\ub358\uac70\uc5d0 \ube44\ud574 \ud504\ub9ac\ud339\uc744 \ub9cc\ub4dc\ub294\uac74 \uc544\uc8fc \uac04\ub2e8\ud558\ub2e4, \uadf8\ub0e5 **\ud558\uc774\uc5b4\ub77c\ud0a4\uc5d0 \uc788\ub294 \uac8c\uc784 \uc624\ube0c\uc81d\ud2b8\ub97c \ucb48\uc6b1 \uc7a1\uc544\uc11c \ud504\ub85c\uc81d\ud2b8 \ud3f4\ub354\ub85c** \ub04c\uc5b4\ub2e4 \ub193\uc73c\uba74 \uc704\uc640 \uac19\uc774 \ubb3c\uc5b4\ubcf8\ub2e4\\r\\n\\r\\n\uc5ec\uae30\uc11c \ud2b9\ubcc4\ud55c \ubaa9\uc801\uc774\ub098 \uc774\uc720\uac00 \uc788\ub294\uac8c \uc544\ub2cc \ud55c **Prefab Variant**\ub97c \ub20c\ub7ec\uc11c \ub9cc\ub4e4\uba74 \ub41c\ub2e4, \uadf8\ub7fc \uc0c1\ub2e8 \ud504\ub9ac\ud339\uacfc \uacc4\uc18d \uc5f0\uacb0\uc774 \uc720\uc9c0\ub418\uc5b4 \uc544\uae4c\uc640 \uac19\uc740 \ud61c\ud0dd\uc744 \ubcfc \uc218 \uc788\ub2e4\\r\\n\\r\\n\ub9cc\ub4dc\ub294\uac74 \uc5b4\ub835\uc9c0 \uc54a\uace0 \uc55e\uc11c \ubc30\uc6e0\ub358 **\ud504\ub9ac\ud339\uacfc\uc758 \uad00\uacc4\uc640 \uad6c\uc870\ub97c \uc124\uacc4\ud558\uace0 \ubcc0\uacbd\uc0ac\ud56d\ub4e4\uc744 \uc798 \uad00\ub9ac\ud558\ub294\uac8c \ub354 \uc911\uc694\ud55c \ud3ec\uc778\ud2b8**\uc774\uae30 \ub54c\ubb38\uc5d0 \uadf8\ub807\ub2e4\\r\\n\\r\\n\ub9cc\uc57d \uccab\ubc88\uc9f8 \uc624\ub9ac\uc9c0\ub110 \ud504\ub9ac\ud339\uc744 \ub9cc\ub4e4\uac8c \ub41c\ub2e4\uba74 \uc5b4\ub5a4 \uc77c\ub4e4\uc774 \ubc8c\uc5b4\uc9c0\uac8c \ub418\ub294\uac78\uae4c?\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Prefab](./prefab/Unity_Prefab_009.jpg)\\r\\n\\r\\n\uc704\uc640 \uac19\uc774 **\uc624\ub9ac\uc9c0\ub110 \ud504\ub9ac\ud339\uc740 \uc815\ub9d0 \ub3c5\ub9bd\uc801\uc778 \ucd5c\uc0c1\uc704 \ubd80\ubaa8\uc640 \uac19\uc740 \uc874\uc7ac**\uac00 \ub418\ubbc0\ub85c \ub2e4\ub978 \uc694\uc18c\uc5d0 \uc5b4\ub5a0\ud55c \uc601\ud5a5\uc744 \ubc1b\uc9c0 \uc54a\uac8c \ub41c\ub2e4\\r\\n\\r\\n\uc5ec\uae30\uc11c \uc8fc\ubaa9\ud574\uc57c \ub418\ub294 \uc810\uc740 \ub450\ubc88\uc9f8 \uc904\uc5d0\uc11c \ubaa8\ub378 \ud504\ub9ac\ud339\uc740 \uc624\ub9ac\uc9c0\ub110 \ud504\ub9ac\ud339\uc774 \ub418\uc5b4\ubc84\ub9b0 \ud558\uc704 \ud504\ub9ac\ud339\uc5d0\uac8c \uc601\ud5a5\uc744 \uc8fc\uc9c0 \ubabb\ud55c\ub2e4\ub294 \uac78 \ubcfc \uc218 \uc788\ub2e4\\r\\n\\r\\n\uc61b\ub0a0 BOOTH \uc544\ubc14\ud0c0\ub4e4\uc740 \uc800\ub7f0 \uc2dd\uc73c\ub85c \uc624\ub9ac\uc9c0\ub110 \ud504\ub9ac\ud339\uc73c\ub85c \uc0dd\uc131\uc744 \ud558\uc600\uae30\uc5d0, \ub300\ud615 \ubaa8\ub378 \ud328\uce58\ub97c \ud574 \ubc84\ub9ac\uba74 \uc544\uc608 \uc0c8\ub85c \uc791\uc131\uc744 \ud574\uc11c \ubc30\ud3ec\ub97c \ud574\uc57c \ud588\ub2e4\\r\\n\\r\\n\uc18c\ube44\uc790\ub4e4\ub3c4 \uc9c0\uae08 \uacaa\uc744 \uc218 \uc788\ub294\uac8c **\ubaa8\ub378 \ud30c\uc77c\uc744 \ud328\uce58\ud588\ub294\ub370 \uc544\ubc14\ud0c0\uac00 \uc0ac\ub77c\uc84c\uc5b4\uc694!** \uac19\uc740 \ub0b4\uc6a9\uc774 \ub300\ud45c\uc801\uc778 \uc0ac\ub840\uc774\ub2e4, \uadf8\ub798\uc11c VRChat \uac15\uc88c\ub4e4 \uc911\uc5d0 [ReassignBoneWeigthsToNewMesh](https://gist.github.com/emilianavt/721cd4dd2d4a62ba54b002b63f894dbf) \uc640 \uac19\uc740 \uc2a4\ud06c\ub9bd\ud2b8\ub97c \ud65c\uc6a9\ud574\uc11c \uae68\uc9c0\uac70\ub098 \uc548 \ubcf4\uc774\ub294 \uc544\ubc14\ud0c0\ub97c \ubcf5\uad6c\ud558\uace0 \uadf8\ub7ac\ub358 \uc774\uc720\uac00 \ubc14\ub85c \uc774\ub7ec\ud55c \ubb38\uc81c\uac00 \uc788\uae30 \ub54c\ubb38\uc774\ub2e4\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \uc544\ubc14\ud0c0 \ubc0f \uc758\uc0c1 \uc81c\uc791\uc790\ub97c \uc704\ud55c \ud504\ub9ac\ud339 \ub0b4\uc6a9\\r\\n\\r\\n\uc758\uc678\ub85c \uc81c\uc791\uc790\ub4e4 \uc911\uc5d0\uc11c\ub3c4 \uc774\ub7ec\ud55c \ub0b4\uc6a9\uc744 \ubab0\ub77c\uc11c \ubaa8\ub378 Prefab \uae30\ubc18\uc778\ub370\ub3c4 Original Prefab\uc73c\ub85c \ud574 \ub450\ub294 \ucf00\uc774\uc2a4\uac00 \uc815\ub9d0 \ub9ce\uc74c\\r\\n\\r\\n\uc81c\uc791\uc790\ub4e4\uc774 \uc5b4\ub824\uc6cc \ud558\ub294 \ud30c\ud2b8 \uc911\uc5d0 \ud558\ub098\uac00 \uc720\uc9c0\ubcf4\uc218 \ubd84\uc57c\uc778\ub370, \uc774\ub7ec\ud55c \uac1c\ub150\ub4e4\uc744 \uc774\ud574\ud558\uace0 \uc801\uc6a9\ud574 \ub450\uba74 \ubaa8\ub378\uc744 \uc218\uc815\ud558\uac70\ub098 \ub2e4\uc591\ud55c \ubc14\ub9ac\uc5b8\ud2b8\uac00 \uc788\uc5b4\ub3c4 \ud55c \ubc88\ub9cc \uc798 \uc218\uc815\ud558\uba74 \ub418\ub2c8\uae4c, \uc218\uc815\uc5d0 \ub300\ud55c \ubd80\ub2f4\uac10\uc774 \uc904\uc5b4\ub4e4\uc5b4\uc11c \ud3b8\ud568\\r\\n\\r\\n\ud2b9\ud788 \uc544\ubc14\ud0c0 \uc81c\uc791\uc790\ub77c\uba74 Variant Prefab\uc73c\ub85c \ubc30\ud3ec\ud558\ub294\uac8c \uc911\uc694\ud568, \uc694\uc998 \ud398\uc774\uc15c\uacfc \uac19\uc740 \ubaa8\ub378\uc744 \uc218\uc815\ud558\ub294 \uad00\ub828 \uc0c1\ud488\ub4e4\ub3c4 \ub9ce\uc774 \ud310\ub9e4 \ub418\ub294\ub370 \ubaa8\ub378 \ud30c\uc77c\uc744 \uc218\uc815\ud558\uba74 Unity\uc5d0\ub3c4 \uc81c\ub300\ub85c \ubc18\uc601\uc774 \ub418\ub2c8\uae4c \uad6c\ub9e4\uc790\ub4e4\uc774 \ubc84\uadf8 \uc5c6\uc774 \uc801\uc6a9\ud558\uae30 \uc88b\uc544\uc9d0\\r\\n\\r\\n\ubc18\ub300\ub85c \ub9d0\ud558\uc790\uba74 \uc81c\uc791\ud560 \ub54c\uc5d0 FBX \ubaa8\ub378 \ud30c\uc77c\uc744 \uc218\uc815\uc744 \ub9ce\uc774 \ud558\uac8c \ub418\ub294\ub370, **\ub9e4\ubc88 \ub2e4\uc2dc Unity \uc138\ud305\ud558\ub294 \uac83\ubcf4\ub2e4 FBX \ud30c\uc77c\uc744 \ub36e\uc5b4\uc50c\uc6cc\ub3c4 \ub418\ub2c8\uae4c \ud6e8\uc52c \ube60\ub974\uac8c \uc791\uc5c5\uc744 \uc9c4\ud589\uc744 \ud560 \uc218 \uc788\uc5b4\uc11c \ud3b8\ud568**, \uadf8\ub807\uc9c0 \uc54a\uc73c\uba74 \uc544\ub798\uc758 \ubc29\uc2dd\uc73c\ub85c \ub9e4\ubc88 \ubcf5\uc0ac \ud574 \uc918\uc57c \ud558\ub294\ub370 \uc740\uadfc \uac12\uc774 \ub2ec\ub77c\uc9c8 \ub54c\ub3c4 \uc788\uc5b4\uc11c \uc624\ud788\ub824 \ub36e\uc5b4\uc50c\uc6b0\ub294\uac8c \ub354 \ube60\ub974\uace0 \uc548\uc815\uc801\uc784, \ud734\uba38\ub178\uc774\ub4dc \ubcf8\uc744 \uc218\uc815\ud55c \uacbd\uc6b0\uc5d0\ub294 \uc57d\uac04\uc758 \ud2b8\ub9ad\ub3c4 \ud544\uc694\ud55c\ub370 \uc5ec\uae30 \uc8fc\uc81c\ub791\uc740 \uc548 \ub9de\uc73c\ub2c8 \uc0dd\ub7b5\\r\\n\\r\\n**\uae30\uc874 Original Prefab\uc744 \ubcc0\ud658\ud558\ub294 \ubc29\ubc95\uc740 \ubaa8\ub378 Prefab\uc5d0\uc11c \ucc98\uc74c\ubd80\ud130 \ub2e4\uc2dc \uc2dc\uc791\ud574\uc11c \uc7ac\uad6c\ucd95\ud558\ub294 \ubc29\ubc95\uc774 \uc81c\uc77c \uae54\ub054**\ud568, \ub2f9\uc5f0\ud788 \ud558\ub098\ud558\ub098 \ub2e4\uc2dc \ud558\uae30 \ubc88\uac70\ub85c\uc6b0\ub2c8\uae4c **[Pumkins Avatar Tools](https://github.com/rurre/PumkinsAvatarTools) \uc560\ub4dc\uc628\uc744 \ud65c\uc6a9\ud558\uc5ec \ubcc0\uacbd\uc0ac\ud56d\ub4e4\uc744 \uadf8\ub300\ub85c \ubcf5\uc0ac** \ud574 \uc8fc\ub294 \uae30\ub2a5\uc774 \uc788\uc73c\ub2c8 \uc774\uac78 \uc4f0\uba74 \ud3b8\ud568, \uadf8\ub9ac\uace0 \uc704\uc758 \ub0b4\uc6a9\ub4e4\uc744 \ubc14\ud0d5\uc73c\ub85c \ud544\uc694 \uc5c6\ub294 \ub0b4\uc6a9 \ub2e4\uc2dc \ub418\ub3cc\ub9ac\uace0 \uaf2d \ud544\uc694\ud55c \ub0b4\uc6a9\ub9cc \ub0a8\uae30\uba74 \uae54\ub054\ud558\uac8c \uc644\uc131\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ud6c4\uae30\\r\\n\\r\\n\uc740\uadfc \uc81c\uc791\uc790\ub4e4\ub3c4 \uc798 \ubab0\ub77c\uc11c \uc774\ub7ec\ud55c \ub0b4\uc6a9\uc744 \uc124\uba85 \ud574 \uc904 \ub54c\ub3c4 \ub9ce\uace0, \uad00\ub828\ud574\uc11c \uc0dd\uae30\ub294 \ubb38\uc81c\ub4e4\ub85c \uc0ac\ub78c\ub4e4\uc774 \uc9c8\ubb38 \uae00\uc744 \uc62c\ub9ac\ub294 \uac78 \uac04\uac04\ud788 \ubd10\uc11c \uc0ac\ub78c\ub4e4\uc774 \uc54c\uc558\uc73c\uba74 \uc88b\uaca0\ub2e4 \uc2f6\uc740 \uac1c\ub150\uc774\ub77c\uc11c \uc801\uc5b4\ubd24\uc74c"},{"id":"intro","metadata":{"permalink":"/ja/blog/intro","source":"@site/blog/intro.md","title":"\ube14\ub85c\uadf8","description":"","date":"2024-08-23T22:03:59.000Z","tags":[],"hasTruncateMarker":false,"authors":[{"name":"\ub9c8\ub07c\uc544\ub610","title":"VRSuya \ubaa8\ub378\ub7ec","url":"https://twitter.com/VRC_Macchiato","imageURL":"https://github.com/crestudio.png","key":"macchiato","page":null}],"frontMatter":{"slug":"intro","title":"\ube14\ub85c\uadf8","authors":"macchiato","tags":[]},"unlisted":false,"prevItem":{"title":"Unity Prefab \uac00\uc774\ub4dc","permalink":"/ja/blog/unity_prefab_guide"},"nextItem":{"title":"VRChat \uc544\ubc14\ud0c0 \uc5c5\ub85c\ub4dc \uc5d0\ub7ec \ud574\uacb0\ubc95","permalink":"/ja/blog/vrchat_upload_guide"}},"content":""},{"id":"vrchat_upload_guide","metadata":{"permalink":"/ja/blog/vrchat_upload_guide","source":"@site/blog/2023-07-17-guide-upload.md","title":"VRChat \uc544\ubc14\ud0c0 \uc5c5\ub85c\ub4dc \uc5d0\ub7ec \ud574\uacb0\ubc95","description":"\uc694\uc57d","date":"2023-07-17T00:00:00.000Z","tags":[{"inline":false,"label":"VRChat","permalink":"/ja/blog/tags/vrchat","description":"VRChat \uad00\ub828 \uc544\ud2f0\ud074"},{"inline":false,"label":"\uac15\uc88c","permalink":"/ja/blog/tags/guide","description":"VRChat \uad00\ub828 \uac15\uc88c"},{"inline":false,"label":"Unity","permalink":"/ja/blog/tags/unity","description":"Unity \uad00\ub828 \uc544\ud2f0\ud074"}],"hasTruncateMarker":false,"authors":[{"name":"\ub9c8\ub07c\uc544\ub610","title":"VRSuya \ubaa8\ub378\ub7ec","url":"https://twitter.com/VRC_Macchiato","imageURL":"https://github.com/crestudio.png","key":"macchiato","page":null}],"frontMatter":{"slug":"vrchat_upload_guide","title":"VRChat \uc544\ubc14\ud0c0 \uc5c5\ub85c\ub4dc \uc5d0\ub7ec \ud574\uacb0\ubc95","authors":"macchiato","tags":["vrchat","guide","unity"]},"unlisted":false,"prevItem":{"title":"\ube14\ub85c\uadf8","permalink":"/ja/blog/intro"}},"content":"## \uc694\uc57d\\r\\n\\r\\n- Missing Script \ucef4\ud3ec\ub10c\ud2b8 \ud574\uacb0\\r\\n- \ucef4\ud30c\uc77c \uc5d0\ub7ec\ub09c \ud504\ub85c\uadf8\ub7a8 \uc5c5\ub370\uc774\ud2b8 \ub610\ub294 \uc81c\uac70\\r\\n- VRCSDK \uc815\uc0c1 \uc0c1\ud0dc \ud655\uc778\\r\\n- \uc720\ub2c8\ud2f0 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uae30\ucd08 \uac1c\ub150\\r\\n\\r\\nVRChat \uc544\ubc14\ud0c0\uac00 \uc5c5\ub85c\ub4dc\uac00 \uc548 \ub418\uc5b4\uc11c \ub3c4\uc6c0 \uc694\uccad \uc62c\ub77c\uc624\ub294 \ucf00\uc774\uc2a4\ub4e4\uc744 \uc77d\uc5b4 \ubcf4\uba74 \ub300\ubd80\ubd84 \uc5ec\uae30 \uac00\uc774\ub4dc\uc758 \ubc29\ubc95\uc744 \uc0ac\uc6a9\ud558\uba74 \ud574\uacb0\ub418\ub294 \uacbd\uc6b0\uac00 \ub9ce\uc74c\\r\\n\\r\\n\uc774 \uc678\uc5d0 \ud654\uc774\ud2b8\ub9ac\uc2a4\ud2b8\uc5d0 \uc5c6\ub294 \ucef4\ud3ec\ub10c\ud2b8\ub97c \uc0ac\uc6a9\ud558\uace0 \uc788\uac70\ub098, \uc0ac\uc6b4\ub4dc \uc5d0\uc14b \uc124\uc815\uc744 \ub204\ub77d\ud588\ub2e4\ub358\uac00 \uc774\ub7f0 \uc870\uac74\ub4e4\uc740 \ubcf4\ud1b5 VRCSDK \ud328\ub110\uc5d0\uc11c \uba54\uc2dc\uc9c0\uac00 \ub098\uc624\ub2c8 \uc0dd\ub7b5\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## Missing Script \ucef4\ud3ec\ub10c\ud2b8 \ud574\uacb0\\r\\n\\r\\n![Missing Script](./upload/Unity_Upload_MissingScirpt.jpg)\\r\\n\\r\\n**Missing Script\uac00 \uc788\uc73c\uba74 \uc5c5\ub85c\ub4dc \ub20c\ub7ec\ub3c4 \ubb54\uac00 \uc9c4\ud589\ud558\ub2e4\uac00 \uc544\ubb34 \uc77c\uc774 \uc548 \uc77c\uc5b4\ub098\ub294 \uacbd\uc6b0**\uac00 \ub9ce\uc74c\\r\\n\\r\\n\uc544\ub2c8\uba74 ``Build & Publish for Android`` \uac19\uc774 \uc708\ub3c4\uc6b0 \uc6a9 Unity \ud504\ub85c\uc81d\ud2b8\uc778\ub370 \ud018\uc2a4\ud2b8 \uc6a9\uc73c\ub85c \ube4c\ub4dc\ud558\ub824\ub294 \ubc84\ud2bc\uc73c\ub85c \ubcc0\ud558\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc74c\\r\\n\\r\\n\ud604\uc7ac \ub300\ubd80\ubd84 [\ubaa8\ub4c8\ub7ec \uc544\ubc14\ud0c0](https://modular-avatar.nadena.dev/) \ucef4\ud3ec\ub10c\ud2b8\uac00 \ud3ec\ud568\ub41c \uc758\uc0c1\uacfc \uae30\ubbf9\ub4e4\uc774 \uc62c\ub77c\uc624\ub294\ub370 \ud574\ub2f9 \ucef4\ud3ec\ub10c\ud2b8\ub97c \uc124\uce58\ud558\uc9c0 \uc54a\uc740 \uacbd\uc6b0\uc5d0\ub294 \uc5c5\ub85c\ub4dc\uac00 \ubd88\uac00\ub2a5\ud568\\r\\n\\r\\n\uad6c\ud615 \uc758\uc0c1\uc774\ub098 \uc544\ubc14\ud0c0 \uacbd\uc6b0\uc5d0\ub294 \uc8fc\ub85c [Dynamic Bone(\ub2e4\uc774\ub098\ubbf9 \ubcf8)](https://assetstore.unity.com/packages/tools/animation/dynamic-bone-16743) \uc2dc\uc808 \ucef4\ud3ec\ub10c\ud2b8\uac00 \ub0a8\uc544\uc788\uc5b4\uc11c \uadf8\ub7ec\ub294 \uacbd\uc6b0\uac00 \ub9ce\uc74c\\r\\n\\r\\n\ud574\uacb0\ubc95\uc740 \ubb34\uc2a8 \ucef4\ud3ec\ub10c\ud2b8\uc778\uc9c0 \uc54c\uba74 **\ud574\ub2f9 \uc560\ub4dc\uc628\uc744 \uc124\uce58\ud558\uac70\ub098 Missing Script \ucef4\ud3ec\ub10c\ud2b8\ub97c \uc0ad\uc81c**\ud558\ub294 \ubc29\ubc95\uc778\ub370, \uc5b4\ub5a4 \uc560\ub4dc\uc628\uc774 \ud544\uc694\ud55c\uc9c0 \uc54c\ub824\uba74 \ud574\ub2f9 \uc5d0\uc14b\uc758 \ud398\uc774\uc9c0\ub97c \ucc38\uace0\ud558\uba74 \ub428\\r\\n\\r\\nMissing Script \ucef4\ud3ec\ub10c\ud2b8\ub97c \uc81c\uac70\ud558\ub294 \uc560\ub4dc\uc628\uc740 [lilAvatarUtils(\uc0ad\uc81c)](https://lilxyzw.booth.pm/items/6532787) \uc560\ub4dc\uc628\uc774 \uc788\uace0, \uc5ec\uae30\ub3c4 \uad00\ub828\ud574\uc11c [Cleaner(\uc120\ud0dd+\uc0ad\uc81c)](https://github.com/crestudio/VRSuya-Cleaner)\ub77c\ub294 \uc560\ub4dc\uc628\uc744 \ub9cc\ub4e4\uc5c8\ub294\ub370 \ud3b8\ud55c \ucabd\uc73c\ub85c \uc4f0\uba74 \ub420 \ub4ef\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ucef4\ud30c\uc77c \uc5d0\ub7ec\ub09c \uc2a4\ud06c\ub9bd\ud2b8 \uc81c\uac70\\r\\n\\r\\n![Compile](./upload/Unity_Upload_Compile.jpg)\\r\\n\\r\\n\uadf8\ub9bc\ucc98\ub7fc \ud504\ub85c\uc81d\ud2b8\ub97c \uc5f4\uace0 **Console \ud328\ub110\uc5d0\uc11c Clear \ubc84\ud2bc\uc744 \ub204\ub974\uba74 \uc544\ubb34\uac83\ub3c4 \uc548 \ub0a8\uc544\uc57c** \ud558\ub294\ub370, \ud654\uba74\ucc98\ub7fc \uc624\ub958\uac00 \uacc4\uc18d \ub0a8\ub294\ub2e4\uba74 \ud504\ub85c\uc81d\ud2b8\uc5d0 \uc124\uce58\ub41c \uc5b4\ub5a0\ud55c \uc2a4\ud06c\ub9bd\ud2b8\uc5d0\uc11c \ucef4\ud30c\uc77c \uc5d0\ub7ec\uac00 \ub09c \uac70\uc784\\r\\n\\r\\n\ud574\uacb0 \ubc29\ubc95\uc740 \ub450 \uac00\uc9c0\uc778\ub370\\r\\n\\r\\n- \uc624\ub958\uac00 \ubc1c\uc0dd\ud558\ub294 \uc560\ub4dc\uc628 \ud328\ud0a4\uc9c0\ub97c \ucd5c\uc2e0 \ubc84\uc804\uc73c\ub85c \uc5c5\ub370\uc774\ud2b8\\r\\n- \uc624\ub958\uac00 \ubc1c\uc0dd\ud558\ub294 \uc560\ub4dc\uc628 \ud328\ud0a4\uc9c0\ub97c \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \uc644\uc804 \uc0ad\uc81c\\r\\n\\r\\n\uc774 \ub54c **\uc5c5\ub370\uc774\ud2b8\ub97c \ud558\uac70\ub098 \uc0ad\uc81c \ud560 \ub54c, \ub36e\uc5b4\uc4f0\uae30\ud558\uac70\ub098 \uc77c\ubd80\ub9cc \uc0ad\uc81c\ud558\uc9c0 \ub9d0\uace0 \uc560\ub4dc\uc628 \uc804\uccb4 \ud3f4\ub354\ub97c \uc5c5\ub370\uc774\ud2b8\ud558\uac70\ub098 \uc0ad\uc81c\ud574\uc57c \ud568**\\r\\n\\r\\n\ub300\ubd80\ubd84 \uc560\ub4dc\uc628\uc740 \ub0b4\uc6a9\ubb3c\uc774 \uc11c\ub85c \uc5f0\uacb0\ub418\uc5b4 \uc788\uace0, \ud2b9\ud788 \uc560\ub4dc\uc628\uc744 \uc5c5\ub370\uc774\ud2b8 \ud560 \ub54c \ud30c\uc77c\uc774 \ucd94\uac00/\uc81c\uac70 \ub418\ub294 \ucf00\uc774\uc2a4\uac00 \ub9ce\uc740\ub370 \ub36e\uc5b4\uc4f0\uae30 \ud558\uba74 \uae30\uc874 \ub0b4\uc6a9\uacfc \ucd5c\uc2e0 \ub0b4\uc6a9\uc774 \ub4a4\uc8fd\ubc15\uc8fd \uc11e\uc5ec\uc11c \ucef4\ud30c\uc77c \uc5d0\ub7ec\uac00 \ub098\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc74c, \uc2ec\uc9c0\uc5b4 \ub611\uac19\uc740 \ub0b4\uc6a9\uc744 \uac00\uc9c4 \uc560\ub4dc\uc628\uc744 \ub450 \uac1c \uc774\uc0c1 \uc124\uce58\ud574\ub3c4 \ucef4\ud30c\uc77c \uc5d0\ub7ec\uac00 \ub0a8\\r\\n\\r\\n\uadf8\ub798\uc11c \uc560\ub4dc\uc628\uc744 \uc790\ub3d9\uc73c\ub85c \ud3f4\ub354 \uc9f8\ub85c \uc6d0\ud074\ub9ad \uc5c5\ub370\uc774\ud2b8 \ud574 \uc8fc\uace0 \uad00\ub9ac\ud558\uae30 \ud3b8\ud558\ub77c\uace0 **VCC\uc5d0 \ud328\ud0a4\uc9c0 \uad00\ub9ac\uc790 \uae30\ub2a5\uc774 \uc788\uc73c\ub2c8 \uadf8\uac78 \uc9c0\uc6d0\ud558\ub294 \uc560\ub4dc\uc628\uc744 \uc4f0\ub294\uac8c \uc88b\uc74c**\\r\\n\\r\\n[\ub9c8\uc2dc\uba5c\ub85c \ud53c\uc9c1\uc2a4\ubcf8](https://wataame89.booth.pm/items/4511536) \uac19\uc774 VCC\ub85c \uc5c5\ub370\uc774\ud2b8\ub97c \ud560 \uc218 \uc5c6\ub294 \uc720\ub8cc \uc560\ub4dc\uc628\uc778 \uacbd\uc6b0\ub098, \ub0b4\ubd80\uc5d0 \ubb54\uac00 \ub370\uc774\ud130\ub97c \uc800\uc7a5\ud558\ub294 \ud2b9\uc218 \ucf00\uc774\uc2a4\ub294 \ud3f4\ub354\ub97c \ud1b5\uc9f8\ub85c \ubc31\uc5c5\ud558\uace0 \uc0ad\uc81c\ud55c \ub2e4\uc74c \uc7ac\uc124\uce58\ud558\ub294 \ubc29\uc2dd\uc73c\ub85c \uc9c4\ud589\ud558\uba74 \ubb38\uc81c\uac00 \uc0dd\uae30\ub354\ub77c\ub3c4 \ud574\uacb0\ud558\uae30 \uc88b\uc74c\\r\\n\\r\\n\ubcf4\ud1b5 \uc704\uc758 \uc791\uc5c5\uc744 \ud558\uba74 \ud574\uacb0 \ub420 \uac70\uace0, \uc5c5\ub370\uc774\ud2b8 \ud574\ub3c4 \ub108\ubb34 \uc624\ub798\ub418\uc5b4\uc11c \ucd5c\uc2e0 VRCSDK\ub97c \uc9c0\uc6d0 \uc548 \ud55c\ub2e4\uba74 \ube44\uc2b7\ud55c \ud234\uc744 \uc0c8\ub85c \ucc3e\uac70\ub098 \ud3ec\uae30\ud558\ub294\uac8c \uc88b\uc74c\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## VRCSDK \uc815\uc0c1 \uc0c1\ud0dc \ud655\uc778\\r\\n\\r\\n![VRCSDK](./upload/Unity_Upload_VRCSDK.jpg)\\r\\n\\r\\n\uc704\uc758 \uc2a4\ud06c\ub9b0\uc0f7\uacfc \ube44\uc2b7\ud558\uac8c VRCSDK\uac00 \uae68\uc838\uc11c **\uc544\ubc14\ud0c0 \ub514\uc2a4\ud06c\ub9bd\ud130\uac00 \uc81c\ub300\ub85c \uc548 \ub098\uc624\uac70\ub098, VRCSDK \uba54\ub274 \uc544\ub798\uc5d0 \uc544\ubb34\uac83\ub3c4 \uc5c6\ub2e4** \uc774\ub7f0 \uacbd\uc6b0\uc5d0\ub294 VRCSDK\ub97c \uc7ac\uc124\uce58\ud574\uc57c \ud568, \uc704\uc758 \ucef4\ud30c\uc77c \uc5d0\ub7ec \ub54c\ubb38\uc5d0 \uac19\uc774 \uae68\uc9c0\ub294 \ucf00\uc774\uc2a4\ub3c4 \uc788\uc5b4\uc11c \ud574\uacb0\ud558\uba74 VRCSDK\uac00 \ubcf5\uad6c\ub418\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc9c0\ub9cc \uac1c\uc778\uc801\uc778 \uacbd\ud5d8\uc73c\ub85c\ub294 VRCSDK\uac00 \uae68\uc9c0\ub294 \uacbd\uc6b0\uc5d0\ub294 \ud504\ub85c\uc81d\ud2b8\ub97c \uc0c8\ub85c \ud30c\uc57c \ud574\uacb0\ub418\ub294 \uacbd\uc6b0\uac00 \ub9ce\uc558\uc74c\\r\\n\\r\\n\uc8fc\ub85c VRCSDK \ubc84\uc804 \ubcc0\uacbd\uc73c\ub85c \uc774\ub807\uac8c \ub418\ub294 \uacbd\uc6b0\uac00 \ub9ce\uc740\ub370, \uc784\uc2dc \ub370\uc774\ud130\uac00 \ub0a8\uc544\uc11c \uadf8\ub7f0\uc9c0 \ud328\ud0a4\uc9c0\ub97c \uc9c0\uc6b0\uace0 \uae68\ub057\ud558\uac8c \uc124\uce58\ud574\ub3c4 \uc5ec\uc804\ud788 \uae68\uc9c0\ub294 \ucf00\uc774\uc2a4\uac00 \ub9ce\uc544\uc11c \uc774\ub7f0 \uacbd\uc6b0\uc5d0\ub294 **\uc720\ub2c8\ud2f0 \uc885\ub8cc\ud558\uace0 \ud504\ub85c\uc81d\ud2b8 \ud3f4\ub354\uc758 Library \ud3f4\ub354\ub97c \ud1b5\uc9f8\ub85c \ub0a0\ub9ac\uace0 \ub2e4\uc2dc \ucf1c \ubd10\uc57c \ud568**\\r\\n\\r\\n<br/>\\r\\n\\r\\n![Library](./upload/Unity_Upload_Library.jpg)\\r\\n\\r\\n\uc704\uc758 Library \ud3f4\ub354\ub97c \uc0ad\uc81c\ud558\uace0 \uc720\ub2c8\ud2f0\ub97c \ucf1c\uba74 \ucc98\uc74c\ubd80\ud130 \uc2a4\ud06c\ub9bd\ud2b8\ub4e4\uc744 \ucef4\ud30c\uc77c\ud558\uace0 \ubaa8\ub4e0 \uc5d0\uc14b\uc744 \ub2e4\uc2dc \ub2e4 \uc720\ub2c8\ud2f0 \uac8c\uc784\uc6a9\uc73c\ub85c \ubd88\ub7ec\uc624\ub294 \uc791\uc5c5\uc744 \ud558\uac8c \ub428\\r\\n\\r\\n\uc774\ub807\uac8c \ud574\ub3c4 \uc548 \ub418\ub294 \uacbd\uc6b0\uc5d0\ub294 \ud504\ub85c\uc81d\ud2b8\ub97c \uc0c8\ub85c \ud30c\ub294\uac8c \uc81c\uc77c \ube60\ub984\\r\\n\\r\\n:::info\\r\\n\\r\\nUnity \ud504\ub85c\uc81d\ud2b8\ub97c \ubc31\uc5c5\ud560 \ub54c \uac70\uc758 \ub300\ubd80\ubd84 \uc720\uc800\ub4e4\uc740 Assets \ud3f4\ub354\ub9cc \ubc31\uc5c5\ud558\uba74 \ub098\uba38\uc9c0\ub294 \uc778\ud130\ub137\uc5d0\uc11c \uad6c\ud558\ub294 \ub370\uc774\ud130\ub4e4\uc774\ubbc0\ub85c \uc774 \ubc29\uc2dd\uc73c\ub85c\ub3c4 \ud504\ub85c\uc81d\ud2b8\ub97c \ubcf5\uad6c \ud560 \uc218 \uc788\ub2e4, \ub530\ub77c\uc11c \uc2dc\uac04\uacfc \uc6a9\ub7c9 \uc808\uac10\uc744 \uc704\ud574\uc11c Assets \ud3f4\ub354\ub9cc \ubc31\uc5c5\ud558\uc790\\r\\n\\r\\n:::\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ud504\ub85c\uc81d\ud2b8 \uc0c8\ub85c \ud30c\ub294 \ubc29\ubc95\\r\\n\\r\\nVCC\uc5d0\uc11c \uc0c8\ub85c\uc6b4 \ud504\ub85c\uc81d\ud2b8\ub97c \ub9cc\ub4e0\ub2e4 \u2192 \ud574\ub2f9 \uc720\ub2c8\ud2f0 \ud504\ub85c\uc81d\ud2b8\ub97c \uc5f0\ub2e4 \u2192 **\uc720\ub2c8\ud2f0\uc5d0\uc11c VRCSDK \ud328\ub110 \uc5f4\uc5b4\uc11c \uc81c\ub300\ub85c \uc5f4\ub9ac\ub294\uc9c0 \uc5d0\ub7ec\uac00 \uc5c6\ub294\uc9c0 \ud655\uc778** \u2192 \uc250\uc774\ub354 \uc784\ud3ec\ud2b8 \u2192 **\uc5d0\ub7ec \uc5c6\ub294\uc9c0 \uccb4\ud06c** \u2192 \uc544\ubc14\ud0c0 \ub370\uc774\ud130\ub9cc \uc62e\uae30\uae30 \u2192 **\uc5d0\ub7ec \uc5c6\ub294\uc9c0 \uccb4\ud06c** \u2192 **\uc560\ub4dc\uc628 \uac01\uac01 \ud558\ub098\uc529 \uc124\uce58**\\r\\n\\r\\n\ud2b9\ud788 **\uc5d0\ub7ec\ub09c \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \ubcf5\uad6c\ud558\ub294 \uacbd\uc6b0\uc5d0\ub294 \uc704\uc758 \uc21c\uc11c\ub97c \ubc18\ub4dc\uc2dc \uc9c0\ucf1c\uc11c \ubcf5\uad6c**\ud558\uc790, \uc5b4\ub514\uc11c \ubb38\uc81c\uac00 \uc0dd\uae30\ub294\uc9c0 \uc54c\uba74 \ubb38\uc81c \ud574\uacb0\ud558\uae30\uac00 \uc27d\uace0 \uadf8\ub9ac\uace0 \ub3c4\uc6c0\uc744 \uc694\uccad\ud560 \ub54c\uc5d0\ub3c4 \uc5b4\ub290 \uacfc\uc815\uc5d0\uc11c \uc5d0\ub7ec \ub0ac\ub294\uc9c0 \uc54c\ub824\uc8fc\uba74 \ub2f5\ubcc0 \ud574 \uc8fc\uae30\ub3c4 \uc88b\ub2e4\\r\\n\\r\\n**\uc5b4\ub514\uc11c \uc5d0\ub7ec\uac00 \ub0ac\ub294\uc9c0 \ud655\uc778\ud558\ub824\uba74 \ubc88\uac70\ub86d\uaca0\uc9c0\ub9cc \uaf2d \uc774\ub807\uac8c \ubcf5\uad6c**\ud558\ub294\uac8c \uc88b\ub2e4\\r\\n\\r\\n\uc644\uc804 \uc544\ubb34 \uac83\ub3c4 \uc5c6\ub294 \ube48 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c VRCSDK\ub3c4 \uc548 \ub418\ub294\uac70\ub294 \ub2e4\uc6b4\ub85c\ub4dc \uc624\ub958\uc774\uac70\ub098 \uc9c4\uc9dc \ucef4\ud4e8\ud130 \ubb38\uc81c\uc77c \uc218 \uc788\ub2e4\\r\\n\\r\\n:::warning\\r\\n\\r\\nVCC\ub97c \ud1b5\ud558\uc9c0 \uc54a\uace0 \uc720\ub2c8\ud2f0 \ud504\ub85c\uc81d\ud2b8\ub97c \ub9cc\ub4dc\ub294 \uacbd\uc6b0, VRCSDK\uac00 \ucef4\ud30c\uc77c \uc644\ub8cc\uac00 \ub418\uc5b4\uc57c \uc758\uc874\uc131 \uc560\ub4dc\uc628\ub4e4\uc774 \uc81c\ub300\ub85c \ucef4\ud30c\uc77c \ub418\ubbc0\ub85c \ub370\ub4dc\ub77d\uc744 \ud53c\ud560\ub824\uba74 \ubc18\ub4dc\uc2dc \uc21c\uc11c\ub97c \uc9c0\ucf1c\uc57c \ud55c\ub2e4\\r\\n\\r\\n:::\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \uc250\uc774\ub354 \ubc0f \uc560\ub4dc\uc628 \uad00\ub9ac\\r\\n\\r\\nVRChat \ucee4\ubba4\ub2c8\ud2f0\uac00 \ucee4\uc9c0\uba74\uc11c \uac01\uc885 \uc250\uc774\ub354\uc640 \uc560\ub4dc\uc628\ub3c4 \ub9ce\uc544\uc9c4 \uc0c1\ud669\uc778\ub370, \uc774\ub7ec\ud55c \ud504\ub85c\uadf8\ub7a8\ub4e4\uc774 \ub9ce\uc544\uc9c8 \uc218\ub85d \uc720\ub2c8\ud2f0 \ud504\ub85c\uc81d\ud2b8\ub3c4 \ubb34\uac70\uc6cc\uc9c0\uace0 \ubb38\uc81c\uac00 \uc0dd\uacbc\uc744 \ub54c \ud574\uacb0\ud558\uae30 \uc5b4\ub824\uc6cc\uc9d0\\r\\n\\r\\n\uc815\ub9ac\ud558\uc790\uba74 **\uc250\uc774\ub354\ub294 \uac00\ub2a5\ud55c 1\uac00\uc9c0 \uc885\ub958\ub85c \ud1b5\ud569\ud574\uc11c \uc904\uc774\uace0, \uc548 \uc4f0\ub294 \uc560\ub4dc\uc628\uc740 \uacfc\uac10\ud788 \uc0ad\uc81c**\ud558\uba74 \ub428\\r\\n\\r\\n\uadf9\ub2e8\uc801\uc73c\ub85c \uae30\ubcf8 \uc250\uc774\ub354\uac00 \ub418\uaca0\uc9c0\ub9cc VRCSDK\ub791 \uc544\ubc14\ud0c0\ub9cc \uc788\uc5b4\ub3c4 \uc5c5\ub85c\ub4dc\uac00 \uac00\ub2a5\ud568, \ub2e4\uc2dc \ub9d0\ud574\uc11c \ub2e4\ub978 \uc560\ub4dc\uc628\ub4e4\uc740 \ud544\uc694\uc5d0 \uc758\ud574\uc11c \uc874\uc7ac\ud574\uc57c \ud558\ub294\uac70\uc9c0, \ud544\uc694 \uc5c6\ub2e4\uba74 \uc9c0\uc6b0\ub294\uac8c \ubb38\uc81c\ub97c \uc77c\uc73c\ud0ac \uc18c\uc9c0\uac00 \uc801\uc5b4\uc9c0\uace0 \uace0\uc9c8\uc801\uc778 \uc720\ub2c8\ud2f0 \ud295\uae40\ub3c4 \ubc29\uc9c0 \ud560 \uc218 \uc788\uc74c\\r\\n\\r\\n\uc65c\ub0d0\ud558\uba74 \ud504\ub85c\uc81d\ud2b8\uc5d0 \uc874\uc7ac\ud558\ub294 \ud504\ub85c\uadf8\ub7a8\ub4e4\uc740 \uc804\ubd80 \ub2e4 \ucef4\ud30c\uc77c \ub418\uc5b4\uc11c \uba54\ubaa8\ub9ac\uc5d0 \uacc4\uc18d \uc874\uc7ac\ud558\ub294\ub370 \uc720\ub2c8\ud2f0\uc5d0\uc11c \ubb54\uac00 \uc791\uc5c5 \ud560 \ub54c\ub9c8\ub2e4 \uc870\uac74\uc774 \ub9de\uc73c\uba74 \ud2b8\ub9ac\uac70\uac00 \ub418\uc5b4\uc11c \uc2e4\ud589 \ub420 \uc218 \uc788\ub294\ub370 \uc774 \ub54c \ubb54\uac00 \uc548 \ub9de\uc73c\uba74 \uc720\ub2c8\ud2f0\uac00 \ud295\uae30\uac70\ub098 \uc624\ub958\uac00 \uc0dd\uae34\ub2e4\uace0 \uc0dd\uac01\ud558\uba74 \ud3b8\ud560 \ub4ef\\r\\n\\r\\n\uc5b4\ucc28\ud53c \ud544\uc694\ud558\uba74 \ub2e4\uc2dc \uae54\uba74 \ub418\ub2c8\uae4c, \uc4f0\uace0 \ub098\uc11c \uc548 \uc4f0\ub294 \uc560\ub4dc\uc628\uc740 \uacfc\uac10\ud558\uac8c \uc9c0\uc6b0\uc790\\r\\n\\r\\n\uc77c \ub54c\ubb38\uc5d0 \uc250\uc774\ub354\ub97c \uc5ec\ub7ec \uac1c \uae54\uc544\ub193\uace0 \uc4f0\ub294\ub370 \ud55c \uac1c\ub85c \ud1b5\uc77c\ud574\uc11c \uc4f8 \ub54c\uc5d0\ub294 \ud55c \ubc88\ub3c4 \uc548 \ud295\uae30\ub358 \uc720\ub2c8\ud2f0\uac00 \uc250\uc774\ub354 \uc5ec\ub7ec \uac1c \uc124\uce58\ub418\uace0 \ub098\ub2c8 \uc790\uafb8 \ud06c\ub798\uc2dc \ub0a8\\r\\n\\r\\n<br/>\\r\\n\\r\\n---\\r\\n\\r\\n## \ud6c4\uae30\\r\\n\\r\\n\uc774 \uac8c\uc784\uc740 \uc544\ubc14\ud0c0 \uafb8\ubbf8\uae30\ub85c \ud3ec\uc7a5\ub41c \uc720\ub2c8\ud2f0 \ub9c8\uc2a4\ud130 \ud2b8\ub808\uc774\ub2dd \ud504\ub85c\uadf8\ub7a8\uc784"}]}}')}}]);